mapper.update:
UPDATE IF_TRS_QMS_SHIPT
SET
TRS_STATUS = #{trsStatus}

    ,IF_INFO_IDX = #{ifInfoIdx}


    ,ERRORLOG_ID = #{errorlogId}

WHERE
IDX = #{idx}

mapper.select:
SELECT LOT_NO,
       PDT_ORDER_NO,
       FINL_STT
FROM MS_MES_FINL_ORDER
WHERE LOT_NO = #{lotNo}
  AND PDT_ORDER_NO = #{pdtOrderNo}

SELECT LOT_NO,
       PHS_ORDER_NO,
       FINL_STT
FROM MS_SRM_FINL_ORDER
WHERE LOT_NO = #{lotNo}
  AND PHS_ORDER_NO = #{phsOrderNo}
  AND ORDER_ITM = #{phsOrderItm}

mapper.update:
UPDATE IF_TRS_QMS_MATERIAL
SET
TRS_STATUS = #{trsStatus}

    ,IF_INFO_IDX = #{ifInfoIdx}


    ,ERRORLOG_ID = #{errorlogId}

WHERE
IDX = #{idx}

mapper.select:
SELECT NVL(MAX(IDX), 0) + 1
FROM IF_TRS_QMS_COSMETIC
WHERE MATERIAL_IDX = #{value}

SELECT NVL(MAX(IDX), 0) + 1
FROM IF_TRS_QMS_COSMETIC_BULK
WHERE MATERIAL_IDX = #{value}

SELECT NVL(MAX(IDX), 0) + 1
FROM IF_TRS_QMS_DRUG
WHERE MATERIAL_IDX = #{value}

SELECT NVL(MAX(IDX), 0) + 1
FROM IF_TRS_QMS_DRUG_BULK
WHERE MATERIAL_IDX = #{value}
