mapper.select:
SELECT WERKS, MATNR, LADGR, MMSTA, EKGRP, INSMK, DISMM, FXHOR, DISPO, DISLS, BSTRF, BSTMI, BESKZ, SOBSL, LGPRO, LGFSB, PLIFZ, WEBAZ, ELSLO, STRGR, MTVFP, FEVOR, KZECH, AWSLS, PRCTR, LOSGR, SOBSK, FVIDK, ZFIRST_GI_DATE, CRT_DS, UDT_DS FROM MS_SAP_MTR_MARC WHERE MATNR IN
#{item.matnr}
AND WERKS IN
#{item.werks}

SELECT MATNR, VKORG, VTWEG, DWERK, AUMNG, KTGRM, CRT_DS, UDT_DS FROM MS_SAP_MTR_MVKE WHERE MATNR IN
#{item.matnr}
AND VKORG IN
#{item.vkorg}
AND VTWEG IN
#{item.vtweg}

mapper.update:
UPDATE MS_SAP_MTR_MVKE
SET DWERK  = #{dwerk},
    AUMNG  = #{aumng},
    KTGRM  = #{ktgrm},
    UDT_DS = SYSDATE
WHERE MATNR = #{matnr}
  AND VKORG = #{vkorg}
  AND VTWEG = #{vtweg}

mapper.select:
SELECT MATNR, CLASS_TYPE, CLAZZ, CHAR_CODE, CHAR_DATA_TYP, CHAR_VAL_CHAR, CHAR_VAL_NUM, CRT_DS, UDT_DS FROM MS_SAP_MTR_ZMDV WHERE MATNR IN
#{item.matnr}
AND CLASS_TYPE IN
#{item.classType}
AND CLAZZ IN
#{item.clazz}
AND CHAR_CODE IN
#{item.charCode}
AND CHAR_DATA_TYP IN
#{item.charDataTyp}

mapper.update:
UPDATE MS_SAP_MTR_ZMDV
SET CHAR_VAL_CHAR = #{charValChar},
    CHAR_VAL_NUM  = #{charValNum},
    UDT_DS        = SYSDATE
WHERE MATNR = #{matnr}
  AND CLASS_TYPE = #{classType}
  AND CLAZZ = #{clazz}
  AND CHAR_CODE = #{charCode}
  AND CHAR_DATA_TYP = #{charDataTyp}

mapper.select:
SELECT MATNR, SPRAS, MAKTX, CRT_DS, UDT_DS FROM MS_SAP_MTR_MAKT WHERE MATNR IN
#{item.matnr}
AND SPRAS IN
#{item.spras}

mapper.update:
UPDATE MS_SAP_MTR_MAKT
SET MAKTX  = #{maktx},
    UDT_DS = SYSDATE
WHERE MATNR = #{matnr}
  AND SPRAS = #{spras}

UPDATE MS_SAP_CHAT_CABN
SET CHAR_CODE_DESC = #{charCodeDesc},
    CHAR_DATA_TYPE = #{charDataType},
    CHAR_VAL_DESC  = #{charValDesc},
    IF_INFO_IDX    = #{ifInfoIdx}
WHERE CHAR_CODE = #{charCode}
  AND CHAR_VAL_CHAR = #{charValChar}
  AND CHAR_VAL_NUM = #{charValNum}

UPDATE MS_SAP_CHAT_KSML
SET CLASS_NAME  = #{className},
    IF_INFO_IDX = #{ifInfoIdx}
WHERE CLASS_TYPE = #{classType}
  AND CLAZZ = #{clazz}
  AND CHAR_CODE = #{charCode}
