mapper.select:
SELECT QLE.PLNT_CD , QLE.LAB_EVT_IDX , QLE.ANS_NO , QLE.ANS_IDX , QLE.RST_SEQ , QLE.PITM_TYP , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QLE.PITM_TYP) AS PITM_TYP_NM , QLE.PITM_CD , QLE.PITM_NM , QLE.ORDER_NO , QLE.LOT_NO , QLE.BATCH_NO , QLE.AMITM_CD , QLE.AMITM_NM , QLE.OCR_DT , QLE.ANS_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = QLE.ANS_UID AND SU.PLNT_CD = QLE.PLNT_CD) AS ANS_UID_NM , QLE.CRTV_MSR , QLE.TST_LOG_RPUB_YN , QLE.REVW_CMMT , QLE.REVW_UID , QLE.REVW_DS , QLE.REVW_IP , QLE.LAB_EVT_APR_IDX , QLE.RJT_UID , QLE.RJT_DS , QLE.RJT_REA , SAI.APR_YN FROM QE_LAB_EVT QLE LEFT JOIN SY_APR_INFO SAI ON QLE.PLNT_CD = SAI.PLNT_CD AND SAI.APR_IDX = QLE.LAB_EVT_APR_IDX WHERE 1 = 1 AND QLE.PLNT_CD = #{plntCd} AND (QLE.LAB_EVT_APR_IDX IS NULL OR SAI.APR_YN = 'N') AND SAI.APR_UID IS NULL -- AND QLE.LAB_EVT_APR_IDX IS NULL -- AND QLE.RJT_UID IS NULL -- AND QLE.RJT_DS IS NULL -- AND QLE.RJT_REA IS NULL
AND QLE.PITM_TYP = #{pitmTyp}
AND UPPER(QLE.PITM_CD) LIKE UPPER('%' || #{pitmCd} || '%')
AND UPPER(QLE.PITM_NM) LIKE UPPER('%' || #{pitmNm} || '%')
AND ANS_NO = #{ansNo}

mapper.update:
UPDATE QE_LAB_EVT SET
    RJT_UID = #{loginUserUid},
    RJT_DS = SYSDATE,
    RJT_REA = #{rjtRea},
    LAB_EVT_APR_IDX = #{labEvtAprIdx}
WHERE 1 = 1
    AND PLNT_CD = #{plntCd}
    AND LAB_EVT_IDX = #{labEvtIdx}
