mapper.select:
SELECT QP.PLNT_CD, QP.SPEC_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = SPEC_PROC_CD) AS SPEC_PROC_NM, QP.USE_VER_YN, QP.RVS_NO, QP.DOC_NO, QP.PITM_CD AS PITM_CD, QP.PITM_VER AS PITM_VER, PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = PITM_TYP AND PLNT_CD = #{plntCd}) AS PITM_TYP_NM, RVS_DT, ENFO_DT, RVS_REA_CD, RVS_CTT, RVS_DIV_PS, REVW_UID, REVW_DS, REVW_IP, PITM_MST_APR_IDX, RJT_UID, RJT_DS, RJT_REA, PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = PITM_TYP AND PLNT_CD = #{plntCd}) AS PITM_TYP_NM, QPI.PITM_NM, PITM_EN, SAP_PRDHA, ANS_DUR_DAY, CRG_DPT_CD, (SELECT DPT_NM FROM SY_DPT WHERE DPT_CD = CRG_DPT_CD AND PLNT_CD = #{plntCd}) AS CRG_DPT_NM, SPCM_NO, MIC_YN, CLLT_MTD, CLLT_MCN, CLLT_PLA, SMP_STRG_MTD, SMP_VOL_UNIT, SMP_VOL_ANS, SMP_VOL_ETC, SMP_VOL_STRG, MTR_TYP, MTR_MRP, LAB_NO, BRD_ABBR, NOM_MTR, ETR_CTN_QTY, RMTR_SPEC, NBR, FTN_YN, PCS01, PCS02, PCS03, PCS04, USE_TRM, OTC_PRD, DMS_EPT_YN, PEAR_DIV, MKR_VOL, MKR_VOL_UNIT, ETN_ANS_REQ, CTRPT_NO, PER_NO, CHAG_VOL, CHAG_VOL_UNIT, DIO_YN, PRB_IN_YN, PRB_FE_YN, PNX_FE_YN, PKG_MTR_SPEC, RMTR_CFMUL FROM QM_PITM QP JOIN QM_PITM_INFO QPI ON QP.PLNT_CD = QPI.PLNT_CD AND QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER JOIN QM_PITM_INFO_SAP QPIS ON QP.PLNT_CD = QPIS.PLNT_CD AND QP.PITM_CD = QPIS.PITM_CD AND QP.PITM_VER = QPIS.PITM_VER JOIN SY_APR_INFO SAI ON QP.PITM_MST_APR_IDX = SAI.APR_IDX
DEL_YN = 'N' AND SPEC_PROC_CD = #{specProcCd} AND APR_REQ_UID = #{aprReqUid}
AND PITM_TYP = #{pitmTyp}
AND QP.PLNT_CD = #{plntCd}
AND QP.PITM_CD LIKE '%' || UPPER(#{pitmCd}) || '%'
AND QPI.PITM_NM LIKE '%' || UPPER(#{pitmNm}) || '%'
ORDER BY QP.CRT_DS DESC

SELECT NVL(MAX(AITM_SPEC_IDX), 0) AS NEW_AITM_SPEC_IDX
FROM QM_PKGA
WHERE PLNT_CD = #{plntCd}
  AND SAP_PRDHA = #{sapPrdha}
  AND SPEC_PROC_CD = #{specProcCd}

mapper.update:
UPDATE QM_PITM
SET SPEC_PROC_CD = #{specProcCd},
    USE_VER_YN   = #{useVerYn}
WHERE PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}

UPDATE QM_PITM
SET SPEC_PROC_CD = #{specProcCd},
    RJT_UID      = #{rjtUid},
    RJT_DS       = SYSDATE,
    RJT_REA      = #{rjtRea},
    USE_VER_YN   = 'N'
WHERE PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}

UPDATE QM_PITM
SET SPEC_PROC_CD = #{specProcCd},
    USE_VER_YN   = #{useVerYn}
WHERE PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}

UPDATE QM_PITM_SPEC
SET USE_VER_YN = #{useVerYn}
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM_SPEC
SET PITM_VER = #{pitmVer}
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}
