mapper.select:
SELECT QP.PLNT_CD, (SELECT PLNT_NM FROM SY_PLNT WHERE SY_PLNT.PLNT_CD = QP.PLNT_CD ) AS PLNT_NM, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPSSUB.SPEC_PROC_CD) AS SPEC_PROC_NM, QP.PITM_CD, QPSSUB.SPEC_PROC_CD, QP.SPEC_PROC_CD AS QP_SPEC_PROC_CD, QP.USE_VER_YN, QP.RVS_NO, QP.DOC_NO, QP.RVS_DT, QP.ENFO_DT, QP.RVS_REA_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QP.RVS_REA_CD AND PLNT_CD = #{plntCd}) AS RVS_REA_NM, QP.RVS_CTT, QP.RVS_DIV_PS, (SELECT USER_NM FROM SY_USER WHERE USER_ID = QP.REVW_UID AND PLNT_CD = #{plntCd}) AS REVW_NM, (SELECT USER_NM FROM SY_USER WHERE USER_ID = QP.RJT_UID AND PLNT_CD = #{plntCd}) AS RJT_NM, QP.REVW_UID, QP.REVW_DS, QP.REVW_IP, QP.PITM_MST_APR_IDX, QP.RJT_UID, QP.RJT_DS, QP.RJT_REA, QPI.PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM, PITM_NM, PITM_EN, QPI.SAP_PRDHA, QPI.ANS_DUR_DAY, QPI.CRG_DPT_CD, (SELECT DPT_NM FROM SY_DPT WHERE DPT_CD = QPI.CRG_DPT_CD AND PLNT_CD = #{plntCd}) AS CRG_DPT_NM, QPI.SPCM_NO, QPI.MIC_YN, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = CLLT_MTD AND PLNT_CD = #{plntCd}) AS CLLT_MTD_NM, QPI.CLLT_MTD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = CLLT_MCN AND PLNT_CD = #{plntCd}) AS CLLT_MCN_NM, QPI.CLLT_MCN, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = CLLT_PLA AND PLNT_CD = #{plntCd}) AS CLLT_PLA_NM, QPI.CLLT_PLA, QPI.SMP_STRG_MTD, QPI.SMP_VOL_UNIT, QPI.SMP_VOL_ANS, SMP_VOL_ETC, QP.PITM_VER, MTR_TYP, MTR_MRP, LAB_NO, BRD_ABBR, NOM_MTR, ETR_CTN_QTY, RMTR_SPEC, NBR, FTN_YN, PCS01, PCS02, PCS03, PCS04, USE_TRM, OTC_PRD, DMS_EPT_YN, PEAR_DIV, MKR_VOL, MKR_VOL_UNIT, ETN_ANS_REQ, CTRPT_NO, PER_NO, CHAG_VOL, CHAG_VOL_UNIT, DIO_YN, PRB_IN_YN, PRB_FE_YN, PNX_FE_YN, PKG_MTR_SPEC, RMTR_CFMUL, COUNT(*) OVER() AS TOTAL FROM QM_PITM QP JOIN QM_PITM_INFO QPI ON QP.PLNT_CD = QPI.PLNT_CD AND QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER JOIN QM_PITM_INFO_SAP QPIS ON QP.PLNT_CD = QPIS.PLNT_CD AND QP.PITM_VER = QPIS.PITM_VER AND QP.PITM_CD = QPIS.PITM_CD JOIN (SELECT MAX(PITM_SPEC_IDX) AS PITM_SPEC_IDX, PITM_VER, PLNT_CD, PITM_CD FROM QM_PITM_SPEC GROUP BY PLNT_CD,PITM_CD,PITM_VER) QPS ON QP.PLNT_CD = QPS.PLNT_CD AND QP.PITM_CD = QPS.PITM_CD AND QP.PITM_VER =QPS.PITM_VER JOIN (SELECT PITM_VER, PLNT_CD, PITM_CD, SPEC_PROC_CD, PITM_SPEC_IDX FROM QM_PITM_SPEC) QPSSUB ON QP.PLNT_CD = QPSSUB.PLNT_CD AND QP.PITM_CD = QPSSUB.PITM_CD AND QP.PITM_VER =QPSSUB.PITM_VER AND QPS.PITM_SPEC_IDX = QPSSUB.PITM_SPEC_IDX
QP.DEL_YN = 'N' AND QP.USE_VER_YN = 'Y'
AND QPI.PITM_TYP = #{pitmTyp}
AND QP.PLNT_CD = #{plntCd}
AND QP.PITM_CD LIKE '%' || UPPER(#{pitmCd}) || '%'
AND QPI.PITM_NM LIKE '%' || UPPER(#{pitmNm}) || '%'
AND QPSSUB.SPEC_PROC_CD = #{specProcCd}
ORDER BY QP.CRT_DS DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.update:
UPDATE QM_PITM_SPEC
SET USE_VER_YN = 'N'
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM_SPEC
SET PITM_VER = #{pitmVer}
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM
SET USE_VER_YN = 'N'
WHERE PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}


    SELECT
    MAX(PITM_SPEC_IDX) AS PITM_SPEC_IDX
    FROM QM_PITM_SPEC
    WHERE PLNT_CD = #{plntCd}
    AND PITM_CD = #{pitmCd}
    AND PITM_VER = #{pitmVer}

UPDATE QM_PITM_SPEC SET
AITM_SPEC_IDX = #{aitmSpecIdx}
WHERE PLNT_CD = #{plntCd}
AND PITM_CD = #{pitmCd}
AND PITM_VER = #{pitmVer}
AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM
SET SPEC_PROC_CD = #{qpSpecProcCd}
  , USE_VER_YN   = #{useVerYn}
WHERE PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}

mapper.insert:

    SELECT
    (SELECT NVL(MAX(PITM_SPEC_IDX) + 1, 1) FROM QM_PITM_SPEC WHERE PLNT_CD = #{plntCd}) AS PITM_SPEC_IDX
    , RVS_NO
    , DOC_NO
    , RVS_DT
    , ENFO_DT
    , RVS_REA_CD
    , RVS_CTT
    , RVS_DIV_PS
    FROM QM_PITM_SPEC
    WHERE PLNT_CD = #{plntCd}
    AND PITM_CD = #{pitmCd}
    AND PITM_SPEC_IDX = #{pitmSpecIdx}

INSERT INTO QM_PITM_SPEC (PLNT_CD, PITM_SPEC_IDX, AITM_SPEC_IDX, PITM_CD
, PITM_VER, USE_VER_YN, DEL_YN, SPEC_PROC_CD
,RVS_NO, DOC_NO, RVS_DT, ENFO_DT, RVS_REA_CD, RVS_CTT, RVS_DIV_PS
) VALUES
(#{plntCd}, #{pitmSpecIdx}, #{aitmSpecIdx}, #{pitmCd}
, #{pitmVer}, #{useVerYn}, #{delYn}, #{specProcCd}
,#{rvsNo} ,#{docNo}, #{rvsDt}, #{enfoDt}, #{rvsReaCd}, #{rvsCtt}, #{rvsDivPs})

mapper.select:
SELECT
SPEC_PROC_CD
, AITM_SPEC_IDX AS OLD_AITM_SPEC_IDX
, SAP_PRDHA
, PITM_SPEC_IDX
FROM (
SELECT
SPEC_PROC_CD
, AITM_SPEC_IDX
, null AS SAP_PRDHA
, PITM_SPEC_IDX
FROM QM_PITM_SPEC
WHERE PITM_SPEC_IDX = (SELECT MAX(PITM_SPEC_IDX)
FROM QM_PITM_SPEC
WHERE PLNT_CD = #{plntCd}
AND PITM_CD = #{pitmCd}
AND PITM_VER = #{pitmVer}
)
AND PLNT_CD = #{plntCd}
UNION
(SELECT SPEC_PROC_CD
, AITM_SPEC_IDX
, SAP_PRDHA
, PITM_SPEC_IDX
FROM (SELECT MAX(AITM_SPEC_IDX) AS AITM_SPEC_IDX
, SAP_PRDHA
FROM QM_PKGA
WHERE PLNT_CD = #{plntCd}
AND SAP_PRDHA = #{sapPrdha}
AND SPEC_PROC_CD = 'S0080400'
GROUP BY SAP_PRDHA) QP,
(SELECT MAX(PITM_SPEC_IDX) AS PITM_SPEC_IDX
, SPEC_PROC_CD
FROM QM_PITM_SPEC
WHERE PLNT_CD = #{plntCd}
AND PITM_CD = #{pitmCd}
AND PITM_VER = #{pitmVer}
GROUP BY SPEC_PROC_CD) QPS
)
)

    WHERE SAP_PRDHA = #{sapPrdha}

SELECT MAX(AITM_SPEC_IDX) AS AITM_SPEC_IDX
FROM QM_PKGA
WHERE PLNT_CD = #{plntCd}
  AND SAP_PRDHA = #{sapPrdha}
  AND SPEC_PROC_CD = #{specProcCd}

mapper.update:
UPDATE QM_PITM_INFO_SAP
SET CTRPT_NO = #{ctId}
WHERE 1 = 1
  AND PLNT_CD = #{plntCd}
  AND PITM_CD = #{pitmCd}
  AND PITM_VER = #{pitmVer}
