mapper.select:
SELECT (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = SCT.HIR_TREE_CD AND PLNT_CD = #{plntCd}) AS MITM_WRK_STUDIO_DIV, SCTR.HIR_TREE_CD AS MITM_PITM_UPPER_DIV, (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = SCTR.HIR_TREE_CD AND PLNT_CD = #{plntCd}) AS UPPER_MITM_PITM_NM, SCTR.TREE_NM AS MITM_PITM_NM, SDP.DPT_NM AS CRG_DPT_NM, SCT.PLNT_CD AS PLNT_CD, SCT.HIR_TREE_CD AS MITM_WRK_PLC_UPPER_DIV, SCT.TREE_NM AS MITM_WRK_PLC_NM, MITM.MITM_CD, MITM.MITM_WRK_PLC_DIV, MITM.MITM_PITM_DIV, MITM.POINT, MITM.ROOMNO, MITM.GRADE, SCD.DTL_NM AS GRADE_NM, MITM.WRK_DIV, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = MITM.WRK_DIV AND PLNT_CD = #{plntCd}) AS WRK_DIV_NM, MITM.ANS_STR_DT, MITM.ANS_CYL_CD, MITM.CRG_DPT_CD, MITM.PER_SPEC, MITM.REVW_DUR_TM, MITM.APR_DUR_TM, MITM.USE_YN, MITM.DEL_YN, (SELECT ANS_CYL_MARK_NM FROM SY_ANS_CYL WHERE PLNT_CD = #{plntCd} AND ANS_CYL_CD = MITM.ANS_CYL_CD) AS ANS_CYL_NM FROM MM_MITM MITM LEFT JOIN SY_CD_TREE SCT ON SCT.TREE_CD = MITM.MITM_WRK_PLC_DIV AND MITM.PLNT_CD = SCT.PLNT_CD LEFT JOIN SY_CD_TREE SCTR ON SCTR.TREE_CD = MITM.MITM_PITM_DIV AND MITM.PLNT_CD = SCTR.PLNT_CD LEFT JOIN SY_ANS_CYL SAC ON SAC.ANS_CYL_CD = MITM.ANS_CYL_CD AND MITM.PLNT_CD = SAC.PLNT_CD LEFT JOIN SY_DPT SDP ON DPT_CD = MITM.CRG_DPT_CD AND SDP.PLNT_CD = SDP.PLNT_CD LEFT JOIN SY_CD_DTL SCD ON SCD.DTL_CD = MITM.GRADE AND MITM.PLNT_CD = SCD.PLNT_CD WHERE MITM.DEL_YN = 'N' AND MITM.USE_YN = 'Y' AND MITM.PLNT_CD = #{plntCd}
AND SCTR.HIR_TREE_CD = #{mitmPitmUpperDiv}
AND MITM.MITM_PITM_DIV = #{mitmPitmDiv}
AND MITM.CRG_DPT_CD = #{crgDptCd}
AND UPPER(MITM.PER_SPEC) LIKE '%' || UPPER(#{perSpec}) || '%'
AND SCT.HIR_TREE_CD IN (SELECT TREE_CD FROM SY_CD_TREE WHERE HIR_TREE_CD IN (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = SCT.HIR_TREE_CD))
AND SCT.HIR_TREE_CD = #{mitmWrkPlcUpperDiv}
AND MITM.MITM_WRK_PLC_DIV = #{mitmWrkPlcDiv}
AND UPPER(MITM.POINT) LIKE '%' || UPPER(#{point}) || '%'
ORDER BY MITM.MITM_CD DESC

mapper.update:
UPDATE MM_MITM
SET DEL_YN = 'Y'
WHERE PLNT_CD = #{plntCd} AND MITM_CD = #{mitmCd}
