mapper.update:
UPDATE MM_MITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
    ,RVS_CTT = #{rvsCtt}
    ,RVS_DT = #{rvsDt}
    ,RVS_DIV_PS = #{rvsDivPs}
    ,RVS_NO = #{rvsNo}
    ,RVS_REA_CD = #{rvsReaCd}
    ,DOC_NO = #{docNo}
    ,ENFO_DT = #{enfoDt}
    ,REVW_DS = SYSDATE
    ,REVW_IP = #{revwIp}
    ,MITM_SPEC_APR_IDX = #{mitmSpecAprIdx}
WHERE PLNT_CD = #{plntCd}
AND MITM_SPEC_IDX = #{mitmSpecIdx}

UPDATE MM_MITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
  ,RJT_DS = SYSDATE
  ,RJT_REA = #{rjtRea}
  ,RJT_UID = #{rjtUid}
WHERE PLNT_CD = #{plntCd}
AND MITM_SPEC_IDX = #{mitmSpecIdx}
