mapper.select:
SELECT MM.PLNT_CD , MM.MITM_CD , MM.MITM_WRK_PLC_DIV , SCTWRK.TREE_NM AS MITM_WRK_PLC_DIV_NM , MM.MITM_PITM_DIV , SCTPITM.TREE_NM AS MITM_PITM_DIV_NM , MM.POINT , MM.ROOMNO , MM.GRADE , SDGRD.DTL_NM AS GRADE_NM , MM.ANS_CYL_CD , SAC.ANS_CYL_MARK_NM AS ANS_CYL_NM , MM.CRG_DPT_CD , SDPT.DPT_NM AS CRG_DPT_NM , MM.ANS_STR_DT , MM.PER_SPEC , MM.REVW_DUR_TM , MM.APR_DUR_TM , MMS.MITM_SPEC_IDX , MMS.AITM_SPEC_IDX , MMS.SPEC_PROC_CD , SDPROC.DTL_NM AS SPEC_PROC_NM , MMS.USE_VER_YN FROM MM_MITM MM LEFT JOIN MM_MITM_SPEC MMS ON MM.PLNT_CD = MMS.PLNT_CD AND MMS.MITM_CD = MM.MITM_CD AND MMS.USE_VER_YN = 'Y' AND MMS.DEL_YN = 'N' LEFT JOIN SY_CD_DTL SDGRD ON MM.PLNT_CD = SDGRD.PLNT_CD AND MM.GRADE = SDGRD.DTL_CD LEFT JOIN SY_CD_DTL SDPROC ON MMS.PLNT_CD = SDPROC.PLNT_CD AND MMS.SPEC_PROC_CD = SDPROC.DTL_CD LEFT JOIN SY_DPT SDPT ON SDPT.PLNT_CD = MM.PLNT_CD AND SDPT.DPT_CD = MM.CRG_DPT_CD LEFT JOIN SY_ANS_CYL SAC ON SAC.PLNT_CD = MM.PLNT_CD AND MM.ANS_CYL_CD = SAC.ANS_CYL_CD LEFT JOIN SY_CD_TREE SCTWRK ON SCTWRK.PLNT_CD = MM.PLNT_CD AND SCTWRK.TREE_CD = MM.MITM_WRK_PLC_DIV LEFT JOIN SY_CD_TREE SCTPITM ON SCTPITM.PLNT_CD = MM.PLNT_CD AND SCTPITM.TREE_CD = MM.MITM_PITM_DIV WHERE MM.PLNT_CD = #{plntCd} AND MM.DEL_YN = 'N' AND MM.USE_YN = 'Y'
AND MMS.SPEC_PROC_CD = #{specProcCd}
AND MM.MITM_CD LIKE '%' || (#{mitmCd}) || '%'
AND MM.CRG_DPT_CD = #{crgDptCd}
AND SCTPITM.TREE_NM LIKE '%' || (#{mitmPitmDivNm}) || '%'

mapper.insert:
INSERT INTO MM_MITM_AITM_SPEC
    (
        PLNT_CD
        , AITM_SPEC_IDX
        ,AITM_SPEC_VER
    )
VALUES
    (
        #{plntCd}
        ,#{aitmSpecIdx}
        ,#{aitmSpecVer}
   )

INSERT INTO MM_MITM_SPEC
    (
        PLNT_CD
        , MITM_SPEC_IDX
        , MITM_CD
        , AITM_SPEC_IDX
        , SPEC_PROC_CD
        , DEL_YN
        , USE_VER_YN
        , RVS_NO
        , DOC_NO
        , RVS_DT
        , ENFO_DT
        , RVS_REA_CD
        , RVS_CTT
        , RVS_DIV_PS
    )
VALUES
    (
        #{plntCd}
        ,#{mitmSpecIdx}
        ,#{mitmCd}
        ,#{aitmSpecIdx}
        ,#{specProcCd}
        ,'N'
        ,'N'
        , #{rvsNo}
        , #{docNo}
        , #{rvsDt}
        , #{enfoDt}
        , #{rvsReaCd}
        , #{rvsCtt}
        , #{rvsDivPs}
    )

mapper.update:
UPDATE MM_MITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
   ,REVW_UID = #{revwUid}
WHERE PLNT_CD = #{plntCd}
AND MITM_SPEC_IDX = #{mitmSpecIdx}

UPDATE MM_MITM_SPEC SET
    DEL_YN = 'Y'
WHERE PLNT_CD = #{plntCd}
AND MITM_SPEC_IDX = #{mitmSpecIdx}

mapper.insert:

    SELECT NVL(MAX(AITM_SEQ)+1,1) FROM MM_MITM_SPEC_AITM WHERE PLNT_CD = #{plntCd} AND AITM_SPEC_IDX = #{aitmSpecIdx}

INSERT INTO MM_MITM_SPEC_AITM
(
     PLNT_CD
    , AITM_SPEC_IDX
    , AITM_SEQ
    , ANS_DPT_CD
    , AMITM_CD
    , AITM_ORD
