mapper.delete:
DELETE FROM MM_MITM_SPEC_AITM
WHERE PLNT_CD = #{plntCd}
AND AITM_SPEC_IDX = #{aitmSpecIdx}

mapper.update:
UPDATE MM_MITM_SPEC SET
    USE_VER_YN = 'N'
WHERE PLNT_CD = #{plntCd}
  AND MITM_CD = #{mitmCd}
  AND USE_VER_YN = 'Y'
