mapper.update:
UPDATE QM_PITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
    ,USE_VER_YN = 'Y'
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}

  UPDATE QM_PITM_SPEC SET
      SPEC_PROC_CD = #{specProcCd}
    ,RJT_DS = SYSDATE
    ,RJT_REA = #{rjtRea}
    ,RJT_UID = #{rjtUid}
  WHERE PLNT_CD = #{plntCd}
AND PITM_SPEC_IDX = #{pitmSpecIdx}
