mapper.select:
SELECT QPS.PLNT_CD , QPS.PITM_SPEC_IDX , QPS.PITM_CD , QPS.AITM_SPEC_IDX , QPS.SPEC_PROC_CD , SDPROC.DTL_NM AS SPEC_PROC_NM , QPS.RVS_NO , QPS.DOC_NO , QPS.RVS_DT , QPS.ENFO_DT , QPS.RVS_REA_CD , QPS.RVS_CTT , QPS.RVS_DIV_PS , QPS.REVW_UID , SU1.USER_NM AS REVW_UNM , QPS.REVW_DS , QPS.PITM_ANS_SPEC_APR_IDX , QPS.RJT_UID , SU2.USER_NM AS RJT_UNM , QPS.RJT_DS , QPS.RJT_REA , QPAS.AITM_SPEC_IDX , QPAS.AITM_SPEC_VER , QPI.PITM_NM , QPI.PITM_EN , QPI.PITM_TYP , PTYP.DTL_NM AS PITM_TYP_NM , QPI.CRG_DPT_CD , SD.DPT_NM AS CRG_DPT_NM , QPI.PITM_VER FROM QM_PITM_SPEC QPS LEFT JOIN QM_PITM QP ON QP.PLNT_CD = QPS.PLNT_CD AND QP.PITM_CD = QPS.PITM_CD AND QPS.PITM_VER = QP.PITM_VER AND QP.USE_VER_YN = 'Y' LEFT JOIN QM_PITM_INFO QPI ON QPI.PLNT_CD = QPS.PLNT_CD AND QPI.PITM_CD = QPS.PITM_CD AND QPS.PITM_VER = QPI.PITM_VER LEFT JOIN QM_PITM_AITM_SPEC QPAS ON QPS.PLNT_CD = QPAS.PLNT_CD AND QPS.AITM_SPEC_IDX = QPAS.AITM_SPEC_IDX LEFT JOIN SY_CD_DTL SDPROC ON QPS.PLNT_CD = SDPROC.PLNT_CD AND QPS.SPEC_PROC_CD = SDPROC.DTL_CD LEFT JOIN SY_CD_DTL PTYP ON QPI.PLNT_CD = PTYP.PLNT_CD AND QPI.PITM_TYP = PTYP.DTL_CD LEFT JOIN SY_DPT SD ON SD.PLNT_CD = QPS.PLNT_CD AND SD.DPT_CD = QPI.CRG_DPT_CD LEFT JOIN SY_USER SU1 ON QPS.REVW_UID = SU1.USER_ID LEFT JOIN SY_USER SU2 ON QPS.RJT_UID = SU2.USER_ID WHERE QPS.PLNT_CD = #{plntCd} AND QPS.DEL_YN = 'N' AND QPS.SPEC_PROC_CD IN (${specProcCd}) AND QP.DEL_YN = 'N' AND QPS.REVW_UID = #{revwUid}
AND QPI.CRG_DPT_CD = #{crgDptCd}
AND QPI.PITM_TYP = #{pitmTyp}
AND QPI.PITM_NM LIKE '%' || #{pitmNm} || '%'

mapper.update:
UPDATE QM_PITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
  ,RJT_DS = SYSDATE
  ,RJT_REA = #{rjtRea}
  ,RJT_UID = #{rjtUid}
  ,REVW_IP = #{revwIp}
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
    ,RVS_CTT = #{rvsCtt}
    ,RVS_DT = #{rvsDt}
    ,RVS_DIV_PS = #{rvsDivPs}
    ,RVS_NO = #{rvsNo}
    ,RVS_REA_CD = #{rvsReaCd}
    ,DOC_NO = #{docNo}
    ,ENFO_DT = #{enfoDt}
    ,REVW_DS = SYSDATE
    ,REVW_IP = #{revwIp}
    ,PITM_ANS_SPEC_APR_IDX = #{pitmAnsSpecAprIdx}
WHERE PLNT_CD = #{plntCd}
  AND PITM_SPEC_IDX = #{pitmSpecIdx}
