mapper.select:
SELECT QP.PLNT_CD , QP.PITM_CD , QP.PITM_VER , QP.SPEC_PROC_CD AS QP_SPEC_PROC_CD , PSPC.DTL_NM AS QP_SPEC_PROC_NM , QPSSUB.SPEC_PROC_CD , SPC.DTL_NM AS SPEC_PROC_NM , QP.DEL_YN , QP.USE_VER_YN , QP.RVS_REA_CD , RRC.DTL_NM AS RVS_REA , QP.PITM_MST_APR_IDX , QPI.PITM_TYP , PT.DTL_NM AS PITM_TYP_NM , QPI.PITM_NM , QPI.PITM_EN , QPI.SAP_PRDHA , QPI.ANS_DUR_DAY , QPI.CRG_DPT_CD , SD.DPT_NM AS CRG_DPT_NM , QPI.SPCM_NO , QPI.MIC_YN , QPS.PITM_SPEC_IDX , QPSSUB.AITM_SPEC_IDX , QPSSUB.USE_VER_YN AS OPS_SEPC_USE_VER_YN FROM QM_PITM QP LEFT JOIN QM_PITM_INFO QPI ON QP.PLNT_CD = QPI.PLNT_CD AND QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER LEFT JOIN QM_PITM_INFO_SAP QPIS ON QP.PLNT_CD = QPIS.PLNT_CD AND QP.PITM_CD = QPIS.PITM_CD AND QP.PITM_VER = QPIS.PITM_VER LEFT JOIN (SELECT PLNT_CD, PITM_CD, PITM_VER, MAX(PITM_SPEC_IDX) AS PITM_SPEC_IDX FROM QM_PITM_SPEC WHERE DEL_YN = 'N' GROUP BY PLNT_CD, PITM_CD, PITM_VER) QPS ON QP.PLNT_CD = QPS.PLNT_CD AND QP.PITM_CD = QPS.PITM_CD AND QP.PITM_VER = QPS.PITM_VER LEFT JOIN (SELECT PLNT_CD, SPEC_PROC_CD, AITM_SPEC_IDX, USE_VER_YN, PITM_SPEC_IDX, PITM_CD, PITM_VER FROM QM_PITM_SPEC) QPSSUB ON QPSSUB.PLNT_CD = QP.PLNT_CD AND QPSSUB.PITM_CD = QP.PITM_CD AND QPSSUB.PITM_VER = QP.PITM_VER AND QPSSUB.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX LEFT JOIN SY_CD_DTL RRC ON RRC.PLNT_CD = QP.PLNT_CD AND QP.RVS_REA_CD = RRC.DTL_CD LEFT JOIN SY_CD_DTL SPC ON QPS.PLNT_CD = SPC.PLNT_CD AND QPSSUB.SPEC_PROC_CD = SPC.DTL_CD LEFT JOIN SY_CD_DTL PSPC ON QP.PLNT_CD = PSPC.PLNT_CD AND QP.SPEC_PROC_CD = PSPC.DTL_CD LEFT JOIN SY_CD_DTL PT ON QPI.PLNT_CD = PT.PLNT_CD AND QPI.PITM_TYP = PT.DTL_CD LEFT JOIN SY_DPT SD ON SD.PLNT_CD = QP.PLNT_CD AND QPI.CRG_DPT_CD = SD.DPT_CD WHERE QP.PLNT_CD = #{plntCd} AND QP.DEL_YN = 'N' AND QP.USE_VER_YN = 'Y' AND QP.SPEC_PROC_CD = #{qpSpecProcCd}
AND QPI.CRG_DPT_CD = #{crgDptCd}
AND QPI.PITM_TYP = #{pitmTyp}
AND QPI.PITM_NM LIKE '%' || #{pitmNm} || '%'
AND QP.PITM_CD LIKE '%' || #{pitmCd} || '%'
AND QPSSUB.USE_VER_YN = #{opsSpecUseVerYn}
AND QPSSUB.SPEC_PROC_CD = #{specProcCd}
ORDER BY PITM_CD, PITM_SPEC_IDX

SELECT
    DPT_CD AS VALUE
   , DPT_NM AS LABEL
   , HIR_DPT_CD AS VALUE_OF_HIR
FROM SY_DPT
WHERE PLNT_CD = #{plntCd}
AND LEVEL = 3
START WITH HIR_DPT_CD IS NULL AND PLNT_CD = #{plntCd}
CONNECT BY PRIOR DPT_CD = HIR_DPT_CD AND PLNT_CD = #{plntCd}

mapper.update:
UPDATE QM_PITM_SPEC SET
    SPEC_PROC_CD = #{specProcCd}
   ,REVW_UID = #{revwUid}
WHERE PLNT_CD = #{plntCd}
AND PITM_SPEC_IDX = #{pitmSpecIdx}

UPDATE QM_PITM_SPEC SET
    USE_VER_YN = 'N'
WHERE PLNT_CD = #{plntCd}
AND PITM_SPEC_IDX = #{pitmSpecIdx}

mapper.insert:

    SELECT NVL(MAX(PITM_SPEC_IDX)+1,1) FROM QM_PITM_SPEC WHERE PLNT_CD = #{plntCd}

