mapper.delete:
DELETE FROM QM_PITM_SPEC_AITM
WHERE PLNT_CD = #{plntCd}
AND AITM_SPEC_IDX = #{aitmSpecIdx}
AND AITM_SEQ = #{aitmSeq}

mapper.update:
