, SPEC_PROC_CD = #{specProcCd}
, DEL_YN = 'N'
WHERE PLNT_CD = #{plntCd}
AND PITM_SPEC_IDX = #{pitmSpecIdx}

mapper.insert:

    SELECT NVL(MAX(AITM_SPEC_IDX)+1,1) FROM QM_PITM_AITM_SPEC WHERE PLNT_CD = #{plntCd}

