mapper.select:
SELECT #{plntCd} AS PLNT_CD, LAB_NO, PRD_DIV, IF_DT
FROM MS_ELN_SPEC
WHERE LAB_NO = #{labNo}
AND PRD_DIV = #{prdDiv}

    AND IF_DT BETWEEN #{ifDtParam[0]} AND #{ifDtParam[1]}

SELECT QP.PLNT_CD , QP.PITM_CD , QP.PITM_VER , QP.SPEC_PROC_CD AS QP_SPEC_PROC_CD , PSPC.DTL_NM AS QP_SPEC_PROC_NM , QPS.SPEC_PROC_CD , SPC.DTL_NM AS SPEC_PROC_NM , QP.DEL_YN , QP.USE_VER_YN , QPI.PITM_TYP , PT.DTL_NM AS PITM_TYP_NM , QPI.PITM_NM , QPI.PITM_EN , QPI.CRG_DPT_CD , SD.DPT_NM AS CRG_DPT_NM , QPI.SPCM_NO , QPS.USE_VER_YN AS SPEC_USE_VER_YN , QPS.PITM_SPEC_IDX , QPS.AITM_SPEC_IDX FROM QM_PITM QP LEFT JOIN QM_PITM_INFO QPI ON QP.PLNT_CD = QPI.PLNT_CD AND QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER LEFT JOIN QM_PITM_INFO_SAP QPIS ON QP.PLNT_CD = QPIS.PLNT_CD AND QP.PITM_CD = QPIS.PITM_CD AND QP.PITM_VER = QPIS.PITM_VER LEFT JOIN QM_PITM_SPEC QPS ON QP.PLNT_CD = QPS.PLNT_CD AND QP.PITM_CD = QPS.PITM_CD AND QP.PITM_VER = QPS.PITM_VER AND QPS.PITM_SPEC_IDX = (SELECT MAX(PITM_SPEC_IDX) FROM QM_PITM_SPEC SQPS WHERE SQPS.PLNT_CD = QP.PLNT_CD AND SQPS.PITM_CD = QP.PITM_CD AND SQPS.PITM_VER = QP.PITM_VER) LEFT JOIN SY_CD_DTL SPC ON QPS.PLNT_CD = SPC.PLNT_CD AND QPS.SPEC_PROC_CD = SPC.DTL_CD LEFT JOIN SY_CD_DTL PSPC ON QP.PLNT_CD = PSPC.PLNT_CD AND QP.SPEC_PROC_CD = PSPC.DTL_CD LEFT JOIN SY_CD_DTL PT ON QPI.PLNT_CD = PT.PLNT_CD AND QPI.PITM_TYP = PT.DTL_CD LEFT JOIN SY_DPT SD ON SD.PLNT_CD = QP.PLNT_CD AND QPI.CRG_DPT_CD = SD.DPT_CD LEFT JOIN (SELECT PLNT_CD, PITM_CD, PITM_VER FROM SD_PM_SPCM_ACSR WHERE 1=1 AND DEL_YN = 'N' GROUP BY PLNT_CD, PITM_CD, PITM_VER) SPSA ON QP.PLNT_CD = SPSA.PLNT_CD AND QP.PITM_CD = SPSA.PITM_CD AND QP.PITM_VER = SPSA.PITM_VER WHERE QP.PLNT_CD = #{plntCd} AND QP.DEL_YN = 'N' AND QP.USE_VER_YN = 'Y' AND QP.SPEC_PROC_CD = #{qpSpecProcCd} AND SPSA.PITM_CD IS NULL
AND QPI.CRG_DPT_CD = #{crgDptCd}
AND QPI.PITM_TYP = #{pitmTyp}
AND QPI.PITM_NM LIKE '%' || #{pitmNm} || '%'
AND QP.PITM_CD LIKE '%' || #{pitmCd} || '%'
AND QPS.USE_VER_YN = #{opsSpecUseVerYn}
AND QPS.SPEC_PROC_CD = #{specProcCd}
ORDER BY PITM_CD, PITM_SPEC_IDX
