mapper.select:
SELECT A.AMITM_CD , AITM_CD , A.VRIA_NO , DECODE(A.VRIA_KN, NULL, B.AITM_KN, B.AITM_KN || '(' || A.VRIA_KN || ')')AS VRIA_NM , A.VRIA_KN , A.VRIA_EN , A.RMK , A.USE_YN , A.CRT_IP , A.CRT_DS , A.CRT_UID , A.UDT_IP , A.UDT_DS , A.UDT_UID , B.AITM_KN , B.AITM_EN FROM MS_AMITM A LEFT OUTER JOIN MS_AITM B USING (AITM_CD) WHERE 1 = 1
AND AITM_CD = #{aitmCd}
AND UPPER(A.VRIA_KN) LIKE UPPER('%' || #{vriaKn} || '%')
AND UPPER(A.VRIA_EN) LIKE UPPER('%' || #{vriaEn} || '%')
AND USE_YN = #{useYn}

mapper.insert:
		MERGE INTO
    		MS_AMITM A
    	USING (
    		SELECT 
    			COUNT(AMITM_CD) CNT
    		FROM
    			MS_AMITM
    		WHERE
    			AMITM_CD = #{amitmCd}
    	) B
    	ON 
    		(B.CNT > 0)
    	WHEN MATCHED THEN
    		UPDATE SET
    			 A.VRIA_KN = #{vriaKn}
    			 , A.VRIA_EN = #{vriaEn}
    			 , A.RMK = #{rmk}
    			 , A.USE_YN = #{useYn}
    		WHERE
    			A.AMITM_CD = #{amitmCd}
    	WHEN NOT MATCHED THEN
    		INSERT (
    			AMITM_CD
    			 , AITM_CD
    			 , VRIA_NO
    			 , VRIA_KN
    			 , VRIA_EN
    			 , RMK
    			 , USE_YN
    			 , CRT_IP
    			 , CRT_DS
    			 , CRT_UID
    			 , UDT_IP
    			 , UDT_DS
    			 , UDT_UID
    		)
    		VALUES (
    			(#{aitmCd} || (SELECT NVL(#{vriaNo}, '00') FROM DUAL))
    			 , #{aitmCd}
    			 , #{vriaNo}
    			 , #{vriaKn}
    			 , #{vriaEn}
    			 , #{rmk}
    			 , #{useYn}
    			 , NULL
    			 , SYSDATE
    			 , #{crtUid}
    			 , NULL
    			 , SYSDATE
    			 , #{udtUid}
    		)
