mapper.select:
SELECT PLNT_CD, VDR_CD, VDR_DIV, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = VDR_DIV ) AS VDR_DIV_NM, VDR_NM, VDR_CRG_NM, VDR_CRG_TEL, VDR_ADR, VDR_ADR_DTL, DEL_YN FROM RM_RITM_VDR WHERE PLNT_CD = #{plntCd}
AND VDR_DIV = #{vdrDiv}
AND UPPER(VDR_NM) LIKE ('%' || TRIM(UPPER(#{vdrNm})) || '%')
AND DEL_YN = #{delYn}
ORDER BY PLNT_CD, VDR_NM

mapper.update:
UPDATE RM_RITM_VDR SET
    VDR_CRG_NM = #{vdrCrgNm},
    VDR_CRG_TEL = #{vdrCrgTel},
    VDR_ADR = #{vdrAdr},
    VDR_ADR_DTL = #{vdrAdrDtl}
WHERE VDR_CD = #{vdrCd}

UPDATE RM_RITM_VDR SET
    DEL_YN = 'Y'
WHERE VDR_CD = #{vdrCd}
