mapper.select:
SELECT MMAP.MITM_REQ_IDX , MMAP.MITM_SPEC_IDX , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MMAP.ANS_PROC_CD) AS ANS_PROC_NM , MMAP.REQ_NO , MMAP.REQ_DT , MMAP.ANS_EDT , MMAP.REQ_DS , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MMAP.REQ_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS REQ_DPT_NM , MMAP.RCP_DT , MMAP.RCP_DS , MMAP.REQ_RMK , MM.MITM_CD , STO.HIR_TREE_CD AS MITM_WRK_STUDIO_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = STO.HIR_TREE_CD) AS MITM_WRK_STUDIO_DIV_NM , WRK.HIR_TREE_CD AS UPPER_MITM_WRK_PLC_DIV , STO.TREE_NM AS UPPER_MITM_WRK_PLC_DIV_NM , MM.MITM_WRK_PLC_DIV , WRK.TREE_NM AS MITM_WRK_PLC_DIV_NM , (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) AS UPPER_MITM_PITM_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = PITM.HIR_TREE_CD) AS UPPER_MITM_PITM_DIV_NM , MM.MITM_PITM_DIV , PITM.TREE_NM AS MITM_PITM_DIV_NM , MM.POINT , MM.ROOMNO , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MM.GRADE AND SCD.PLNT_CD = MM.PLNT_CD) AS GRADE , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MM.CRG_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS CRG_DPT_NM , MM.ANS_CYL_CD , (SELECT ANS_CYL_RULE_NM FROM SY_ANS_CYL SAC WHERE SAC.ANS_CYL_CD = MM.ANS_CYL_CD) AS ANS_CYL_RULE_NM , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MM.WRK_DIV AND SCD.PLNT_CD = MM.PLNT_CD) AS WRK_DIV_NM , MMS.AITM_SPEC_IDX FROM MT_MITM_ANS_PROC MMAP LEFT JOIN MM_MITM_SPEC MMS ON MMAP.MITM_SPEC_IDX = MMS.MITM_SPEC_IDX AND MMAP.PLNT_CD = MMS.PLNT_CD LEFT JOIN MM_MITM MM ON MMS.PLNT_CD = MM.PLNT_CD AND MMS.MITM_CD = MM.MITM_CD LEFT JOIN SY_CD_TREE WRK ON WRK.TREE_CD = MM.MITM_WRK_PLC_DIV AND WRK.PLNT_CD = MM.PLNT_CD LEFT JOIN SY_CD_TREE STO ON STO.TREE_CD = WRK.HIR_TREE_CD AND STO.PLNT_CD = MM.PLNT_CD LEFT JOIN SY_CD_TREE PITM ON PITM.TREE_CD = MM.MITM_PITM_DIV AND PITM.PLNT_CD = MM.PLNT_CD WHERE 1=1 AND ANS_PROC_CD = #{ansProcCd} AND MMAP.HLD_YN != 'Y' AND MMAP.PLNT_CD = #{plntCd}
AND TRIM(UPPER(MM.POINT)) LIKE ('%' || TRIM(UPPER(#{point})) || '%')
AND TRIM(UPPER(MM.ROOMNO)) LIKE ('%' || TRIM(UPPER(#{roomno})) || '%')
AND TRIM(UPPER(MM.MITM_CD)) LIKE ('%' || TRIM(UPPER(#{mitmCd})) || '%')
AND MM.CRG_DPT_CD = #{crgDptCd}
AND STO.HIR_TREE_CD = #{mitmWrkStudioDiv}
AND WRK.HIR_TREE_CD = #{upperMitmWrkPlcDiv}
AND MM.MITM_WRK_PLC_DIV = #{mitmWrkPlcDiv}
AND (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) = #{upperMitmPitmDiv}
AND MM.MITM_PITM_DIV = #{mitmPitmDiv}
AND MMAP.REQ_DT BETWEEN #{searchReqDt[0]} AND #{searchReqDt[1]}
ORDER BY MMAP.MITM_REQ_IDX

mapper.update:

    SELECT 'MR' || TO_CHAR(SYSDATE, 'YYMM') || LPAD(NVL(MAX(SUBSTR(ANS_NO, -4))+1,1),4,0) FROM MT_MITM_ANS_PROC WHERE PLNT_CD = #{plntCd}

UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , ANS_NO = #{ansNo}
    , RCP_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , RCP_DS = SYSDATE
    , RCP_DPT_CD = #{rcpDptCd}
    , RCP_UID = #{rcpUid}
    , RCP_RMK = #{rcpRmk}
    , ASS_APR_REQ_IDX = #{assAprReqIdx}
WHERE MITM_REQ_IDX = #{mitmReqIdx}
AND PLNT_CD = #{plntCd}
