mapper.select:
SELECT MMAP.MITM_REQ_IDX , MMAP.MITM_SPEC_IDX , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MMAP.ANS_PROC_CD) AS ANS_PROC_NM , MMAP.REQ_NO , MMAP.REQ_DT , MMAP.ANS_EDT , MMAP.ANS_NO , MMAP.REQ_DS , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = MMAP.REQ_UID AND SU.PLNT_CD = MMAP.PLNT_CD) AS REQ_UNM , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MMAP.REQ_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS REQ_DPT_NM , MMAP.RCP_DT , MMAP.RCP_DS , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = MMAP.RCP_UID AND SU.PLNT_CD = MMAP.PLNT_CD) AS RCP_UNM , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MMAP.RCP_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS RCP_DPT_NM , MMAP.ASS_NO , MMAP.ASS_DT , MMAP.ASS_DS , MMAP.ASS_SPCC , MMAP.CPL_RQM_DT , MMAP.RJT_DS , MMAP.RJT_REA , MMAP.RST_APR_REQ_IDX , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = MMAP.RJT_UID AND SU.PLNT_CD = MMAP.PLNT_CD) AS RJT_UNM , MM.MITM_CD , (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS UPPER_MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV)) AS UPPER_MITM_WRK_PLC_DIV_NM , MM.MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS MITM_WRK_PLC_DIV_NM , (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) AS UPPER_MITM_PITM_DIV_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV)) AS UPPER_MITM_PITM_DIV_NM , MM.MITM_PITM_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV) AS MITM_PITM_DIV_NM , MM.POINT , MM.ROOMNO , MM.GRADE , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MM.CRG_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS CRG_DPT_NM , MM.ANS_CYL_CD , (SELECT ANS_CYL_RULE_NM FROM SY_ANS_CYL SAC WHERE SAC.ANS_CYL_CD = MM.ANS_CYL_CD) AS ANS_CYL_RULE_NM , MMS.AITM_SPEC_IDX FROM MT_MITM_ANS_PROC MMAP LEFT JOIN MM_MITM_SPEC MMS ON MMAP.MITM_SPEC_IDX = MMS.MITM_SPEC_IDX AND MMAP.PLNT_CD = MMS.PLNT_CD LEFT JOIN MM_MITM MM ON MMS.PLNT_CD = MM.PLNT_CD AND MMS.MITM_CD = MM.MITM_CD LEFT JOIN SY_APR_INFO SAI ON SAI.PLNT_CD = MMAP.PLNT_CD AND SAI.APR_IDX = MMAP.RST_APR_REQ_IDX WHERE 1=1 AND ANS_PROC_CD = #{ansProcCd} AND MMAP.PLNT_CD = #{plntCd} AND MMAP.HLD_YN != 'Y' AND SAI.APR_UID = #{userId}
AND TRIM(UPPER(MM.POINT)) LIKE ('%' || TRIM(UPPER(#{point})) || '%')
AND TRIM(UPPER(MM.GRADE)) LIKE ('%' || TRIM(UPPER(#{grade})) || '%')
AND TRIM(UPPER(MM.ROOMNO)) LIKE ('%' || TRIM(UPPER(#{roomno})) || '%')
AND TRIM(UPPER(MM.MITM_CD)) LIKE ('%' || TRIM(UPPER(#{mitmCd})) || '%')
AND MM.MITM_PITM_DIV = #{mitmPitmDiv}
AND MM.ANS_CYL_CD = #{ansCylCd}
AND MM.MITM_WRK_PLC_DIV = #{mitmWrkPlcDiv}
AND (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) = #{upperMitmWrkPlcDiv}
AND (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) = #{upperMitmPitmDiv}
AND MMAP.RCP_DT BETWEEN #{formRcpDt[0]} AND #{formRcpDt[1]}
AND MMAP.ASS_DT BETWEEN #{formAssDt[0]} AND #{formAssDt[1]}

mapper.update:
UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , RJT_UID = #{userId}
    , RJT_DS = SYSDATE
    , RJT_REA = #{rea}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_PROC SET
    HLD_YN = 'Y'
    , HLD_DS = SYSDATE
    , HLD_UID = #{userId}
    , HLD_REA = #{rea}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}
