mapper.select:
SELECT MMAP.PLNT_CD , MMAP.MITM_REQ_IDX , MMAP.MITM_SPEC_IDX , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MMAP.ANS_PROC_CD) AS ANS_PROC_NM , MMAP.REQ_NO , MMAP.REQ_DT , MMAP.ANS_EDT , MMAP.ANS_NO , MMAP.REQ_DS , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MMAP.REQ_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS REQ_DPT_NM , MMAP.RCP_DT , MMAP.RCP_DS , MMAP.ASS_NO , MMAP.ASS_DT , MMAP.SYT_JDG , MM.MITM_CD , (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS UPPER_MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV)) AS UPPER_MITM_WRK_PLC_DIV_NM , MM.MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS MITM_WRK_PLC_DIV_NM , (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) AS UPPER_MITM_PITM_DIV_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV)) AS UPPER_MITM_PITM_DIV_NM , MM.MITM_PITM_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV) AS MITM_PITM_DIV_NM , MM.POINT , MM.ROOMNO , MM.GRADE FROM MT_MITM_ANS_PROC MMAP LEFT JOIN MM_MITM_SPEC MMS ON MMAP.MITM_SPEC_IDX = MMS.MITM_SPEC_IDX AND MMAP.PLNT_CD = MMS.PLNT_CD LEFT JOIN MM_MITM MM ON MMS.PLNT_CD = MM.PLNT_CD AND MMS.MITM_CD = MM.MITM_CD WHERE 1=1 AND ANS_PROC_CD IN (#{testProcessList[0]}, #{testProcessList[1]}) AND MMAP.PLNT_CD = #{plntCd} AND MMAP.HLD_YN != 'Y'
AND TRIM(UPPER(MMAP.REQ_NO)) LIKE ('%' || TRIM(UPPER(#{reqNo})) || '%')
AND TRIM(UPPER(MMAP.ANS_NO)) LIKE ('%' || TRIM(UPPER(#{ansNo})) || '%')
AND TRIM(UPPER(MM.MITM_CD)) LIKE ('%' || TRIM(UPPER(#{mitmCd})) || '%')
AND MM.CRG_DPT_CD = #{crgDptCd}
AND MM.MITM_PITM_DIV = #{mitmPitmDiv}
AND (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) = #{upperMitmPitmDiv}
AND MMAP.REQ_DT BETWEEN #{searchReqDt[0]} AND #{searchReqDt[1]}
AND MMAP.ASS_DT BETWEEN #{searchAssDt[0]} AND #{searchAssDt[1]}

mapper.update:
UPDATE MT_MITM_ANS_PROC SET
    SYT_JDG = #{sytJdg}
WHERE PLNT_CD = #{plntCd} AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , REVW_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , REVW_DS = SYSDATE
    , REVW_CMMT = #{rea}
    , REVW_UID = #{aprUid}
    , SYT_JDG = #{sytJdg}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_RST SET
    RST_VAL = #{rstVal}
    , MARK_VAL = #{markVal}
    , RST_RMK = #{rstRmk}
    , RST_JDG = #{rstJdg}
    , RPT_PRT_SLV_VAL = #{rptPrtSlvVal}
    , RPT_PRT_ITM_NM = #{rptPrtItmNm}
    , RPT_PRT_YN = #{rptPrtYn}
    , ISP_DUR_TM = #{ispDurTm}
    , EQM_USE_TM = #{eqmUseTm}
    , ANS_EQM_CD = #{ansEqmCd}
    , SMP_CLLT_DT = #{smpClltDt}
    , CLLT_TM_STR = #{clltTmStr}
    , CLLT_TM_END = #{clltTmEnd}
    , SMP_CLLT_QTY = #{smpClltQty}
    , SMP_CLLT_UNIT = #{smpClltUnit}

    , RST_PROC_CD = #{rstProcCd}

WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}
AND RST_SEQ = #{rstSeq}

UPDATE MT_MITM_ANS_PROC SET
    HLD_YN = 'Y'
    , HLD_DS = SYSDATE
    , HLD_UID = #{userId}
    , HLD_REA = #{rea}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}
