mapper.select:
SELECT MMAP.MITM_REQ_IDX , MMAP.MITM_SPEC_IDX , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = MMAP.ANS_PROC_CD) AS ANS_PROC_NM , MMAP.REQ_NO , MMAP.REQ_DT , MMAP.ANS_EDT , MMAP.ANS_NO , MMAP.REQ_DS , (SELECT DPT_NM FROM SY_DPT SD WHERE SD.DPT_CD = MMAP.REQ_DPT_CD AND SD.PLNT_CD = MMAP.PLNT_CD) AS REQ_DPT_NM , MMAP.RCP_DT , MMAP.RCP_DS , MMAP.ASS_NO , MMAP.ASS_DT , MMAP.RJT_DS , MMAP.RJT_REA , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = MMAP.RJT_UID AND SU.PLNT_CD = MMAP.PLNT_CD) AS RJT_UNM , MM.MITM_CD , (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS UPPER_MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV)) AS UPPER_MITM_WRK_PLC_DIV_NM , MM.MITM_WRK_PLC_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_WRK_PLC_DIV) AS MITM_WRK_PLC_DIV_NM , (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) AS UPPER_MITM_PITM_DIV_DIV , (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = (SELECT HIR_TREE_CD FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV)) AS UPPER_MITM_PITM_DIV_NM , MM.MITM_PITM_DIV , (SELECT TREE_NM FROM SY_CD_TREE SCT WHERE SCT.TREE_CD = MM.MITM_PITM_DIV) AS MITM_PITM_DIV_NM , MM.POINT , MM.ROOMNO , MM.GRADE FROM MT_MITM_ANS_PROC MMAP LEFT JOIN MM_MITM_SPEC MMS ON MMAP.MITM_SPEC_IDX = MMS.MITM_SPEC_IDX AND MMAP.PLNT_CD = MMS.PLNT_CD LEFT JOIN MM_MITM MM ON MMS.PLNT_CD = MM.PLNT_CD AND MMS.MITM_CD = MM.MITM_CD WHERE 1=1 AND MMAP.ANS_PROC_CD IN (#{testProcessList[0]}, #{testProcessList[1]}) AND MMAP.PLNT_CD = #{plntCd} AND MMAP.HLD_YN != 'Y' AND MMAP.REVW_UID = #{revwUid}
AND MM.CRG_DPT_CD = #{crgDptCd}
AND MM.MITM_PITM_DIV = #{mitmPitmDiv}
AND (SELECT HIR_TREE_CD FROM SY_CD_TREE WHERE TREE_CD = MM.MITM_PITM_DIV) = #{upperMitmPitmDiv}
AND MMAP.REQ_DT BETWEEN #{searchReqDt[0]} AND #{searchReqDt[1]}
AND MMAP.ASS_DT BETWEEN #{searchAssDt[0]} AND #{searchAssDt[1]}

mapper.update:
UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , REVW_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , REVW_DS = SYSDATE
    , RST_APR_REQ_IDX = #{rstAprReqIdx}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , RJT_UID = #{userId}
    , RJT_DS = SYSDATE
    , RJT_REA = #{rea}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}

UPDATE MT_MITM_ANS_PROC SET
    HLD_YN = 'Y'
    , HLD_DS = SYSDATE
    , HLD_UID = #{userId}
    , HLD_REA = #{rea}
WHERE PLNT_CD = #{plntCd}
AND MITM_REQ_IDX = #{mitmReqIdx}
