mapper.select:
SELECT QPAP.PLNT_CD , QPAP.ANS_IDX , QPAP.REQ_IDX , QPAP.PITM_SPEC_IDX , QPAP.ANS_PROC_CD , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.ANS_PROC_CD) AS ANS_PROC_CD_NM , QPAP.SYT_JDG , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.SYT_JDG) AS SYT_JDG_NM , QPAP.ANS_TYP , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.ANS_TYP) AS ANS_TYP_NM , QPAP.REQ_DT , QPAP.REQ_DS , QPAP.REQ_DPT_CD , (SELECT SD.DPT_NM FROM SY_DPT SD WHERE SD.PLNT_CD = QPAP.PLNT_CD AND SD.DPT_CD = QPAP.REQ_DPT_CD) AS REQ_DPT_CD_NM , QPAP.REQ_UID , (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QPAP.REQ_UID) AS REQ_UID_NM , QPAP.REQ_RMK , QPAP.REQ_CANL_YN , QPAP.REQ_CANL_DS , QPAP.REQ_CANL_UID , (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QPAP.REQ_CANL_UID) AS REQ_CANL_UID_NM , QPAP.REQ_CANL_REA , QPAP.ANS_EDT , QPAP.ANS_NO , QPAP.RCP_DT , QPAP.RCP_DPT_CD , (SELECT SD.DPT_NM FROM SY_DPT SD WHERE SD.PLNT_CD = QPAP.PLNT_CD AND SD.DPT_CD = QPAP.RCP_DPT_CD) AS RCP_DPT_CD_NM , QPAP.RCP_UID , (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QPAP.RCP_UID) AS RCP_UID_NM , QPAP.RCP_RMK , QPAP.CLLT_DT , QPAP.CLLT_UID , (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QPAP.CLLT_UID) AS CLLT_UID_NM , QPAP.CLLT_RMK , QPAP.ASS_NO , QPAP.ASS_DT , QPAP.ASS_SPCC , QPAP.ASS_APR_REQ_IDX , QPAP.CPL_RQM_DT , QPAP.WRK_STT_CD , (SELECT DTL_NM FROM SY_CD_DTL WHERE PLNT_CD = QPAP.PLNT_CD AND DTL_CD = QPAP.WRK_STT_CD) AS WRK_STT_CD_NM , (NVL(QPAI.SMP_VOL_ANS, 0) + NVL(QPAI.SMP_VOL_ETC, 0) + NVL(QPAI.SMP_VOL_STRG, 0)) AS SMP_VOL_TOT , DECODE(QPI.PITM_TYP, #{pitmCdForDecode}, QPAR.SPL_LOT_NO, QPAR.LOT_NO) AS CRT_NO , QPAR.MTR_CD , QPAR.BATCH_NO , QPAR.ETR_QTY , QPAR.INP_UNIT , QPAR.SPL_CD , QPAR.SPL_NM , QPAR.LOT_NO , QPAR.SPL_LOT_NO , QPAR.MAK_DT , QPAR.USE_LMT , QPAR.CSM_NM , QPAR.ETR_DT , QPAI.SMP_LABEL_PRT_IDX , QSLP.LABEL_PRT_IDX , QSLP.PRT_SEQ , QSLP.LABEL_RPT_IDX , QSLP.LABEL_PRT_PROC , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSLP.LABEL_PRT_PROC) AS LABEL_PRT_PROC_NM , QPI.PITM_CD , QPI.PITM_TYP , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM , QPI.PITM_NM , QSM.SMP_DIV_CD , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.SMP_DIV_CD) AS SMP_DIV_CD_NM , SRM.RPT_DIV , (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = SRM.RPT_DIV) AS RPT_DIV_NM FROM QT_PITM_ANS_PROC QPAP INNER JOIN QT_PITM_ANS_INFO QPAI ON QPAP.PLNT_CD = QPAI.PLNT_CD AND QPAP.ANS_IDX = QPAI.ANS_IDX INNER JOIN QT_PITM_ANS_REQ QPAR ON QPAP.PLNT_CD = QPAR.PLNT_CD AND QPAP.REQ_IDX = QPAR.REQ_IDX LEFT OUTER JOIN QM_PITM_SPEC QPS ON QPAP.PLNT_CD = QPS.PLNT_CD AND QPAP.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX LEFT OUTER JOIN QM_PITM_INFO QPI ON QPS.PLNT_CD = QPI.PLNT_CD AND QPS.PITM_CD = QPI.PITM_CD AND QPS.PITM_VER = QPI.PITM_VER LEFT OUTER JOIN QS_SMP_MNG QSM ON QPAP.PLNT_CD = QSM.PLNT_CD AND QPAP.ANS_IDX = QSM.ANS_IDX LEFT OUTER JOIN QS_SMP_LABEL_PRT QSLP ON QPAI.SMP_LABEL_PRT_IDX = QSLP.LABEL_PRT_IDX LEFT OUTER JOIN SY_RPT_MST SRM ON QSLP.LABEL_RPT_IDX = SRM.RPT_IDX WHERE 1 = 1 AND QPAP.PLNT_CD = #{plntCd} AND QSLP.LABEL_PRT_PROC IN
#{item}
AND QPI.PITM_TYP = #{pitmTyp}
AND SRM.RPT_DIV = #{rptDiv}
AND QPAP.ANS_TYP = #{ansTyp}
AND UPPER(QPI.PITM_CD) LIKE UPPER('%' || #{pitmCd} || '%')
AND QPAP.ANS_PROC_CD = #{ansProcCd}
AND UPPER(QPAP.ANS_NO) LIKE UPPER('%' || #{ansNo} || '%')
AND UPPER(QPAR.BATCH_NO) LIKE UPPER('%' || #{batchNo} || '%')
AND UPPER(QPAR.LOT_NO) LIKE UPPER('%' || #{lotNo} || '%')
AND QPAP.RCP_DT BETWEEN #{searchRcpDt[0]} AND #{searchRcpDt[1]}
ORDER BY QPAR.MTR_NM
