mapper.select:
SELECT QPAR.PLNT_CD , QPAR.REQ_IDX , QPAR.ISP_REQ_NO , QPAR.ISP_REQ_DT , QPAR.MTR_CD , QPAR.MTR_NM , QPAR.BATCH_NO , QPAR.ETR_QTY , QPAR.INP_UNIT , QPAR.ETR_DT , QPAR.SPL_CD , QPAR.SPL_NM , QPAR.LOT_NO , QPAR.SPL_LOT_NO , QPAR.MAK_DT , QPAR.CSM_NM , QPAR.UDT_DS , QPAR.REV_DS , QP.PITM_CD , QPI.PITM_NM , QPI.PITM_EN , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM , QP.PITM_VER , QPAP.ANS_IDX , QPAP.ANS_NO , QPAP.OOS_YN , QPAP.SYT_JDG , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPAP.SYT_JDG) AS SYT_JDG_NM , QPAP.REQ_DT , QPAP.RCP_DT , QPAP.RCP_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = QPAP.RCP_UID AND SU.PLNT_CD = QPAP.PLNT_CD) AS RCP_UNM , DECODE(QPAP.CLLT_DT, NULL, SYSDATE, QPAP.CLLT_DT) AS CLLT_DT , QPAP.CLLT_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = QPAP.CLLT_UID AND SU.PLNT_CD = QPAP.PLNT_CD) AS CLLT_UNM , QPAI.SMP_STRG_MTD , QPAI.SMP_VOL_UNIT , (NVL(QPAI.SMP_VOL_ANS, 0) + NVL(QPAI.SMP_VOL_ETC, 0) + NVL(QPAI.SMP_VOL_STRG, 0)) AS SMP_VOL_TOT , QPAI.PKG_MTR_SPEC , QPAI.RMTR_SPEC , QPAI.RMTR_CFMUL , QPAI.RPT_SPCC , QPAI.ARPT_SPCC FROM QT_PITM_ANS_REQ QPAR LEFT JOIN QT_PITM_ANS_PROC QPAP ON QPAR.REQ_IDX = QPAP.REQ_IDX AND QPAR.PLNT_CD = QPAP.PLNT_CD LEFT JOIN QT_PITM_ANS_INFO QPAI ON QPAP.ANS_IDX = QPAI.ANS_IDX AND QPAP.PLNT_CD = QPAI.PLNT_CD LEFT JOIN QM_PITM_SPEC QPS ON QPAP.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX AND QPAR.MTR_CD = QPS.PITM_CD AND QPAP.PLNT_CD = QPS.PLNT_CD LEFT JOIN QM_PITM QP ON QPS.PITM_CD = QP.PITM_CD AND QPS.PITM_VER = QP.PITM_VER AND QPS.PLNT_CD = QP.PLNT_CD LEFT JOIN QM_PITM_INFO QPI ON QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER AND QP.PLNT_CD = QPI.PLNT_CD LEFT JOIN QM_PITM_INFO_SAP QPIS ON QP.PITM_CD = QPIS.PITM_CD AND QP.PITM_VER = QPIS.PITM_VER AND QP.PLNT_CD = QPIS.PLNT_CD WHERE QPAR.PLNT_CD = '1100' AND QPAP.HLD_YN = 'N'
AND QPAP.SYT_JDG = #{sytJdg}
AND QPAP.ANS_PROC_CD = #{ansProcCd}
AND QPI.PITM_TYP = #{pitmTyp}
AND TRIM(UPPER(QPI.PITM_NM)) LIKE ('%' || TRIM(UPPER(#{pitmNm})) || '%')
AND TRIM(UPPER(QPI.PITM_CD)) LIKE ('%' || TRIM(UPPER(#{pitmCd})) || '%')
AND TRIM(UPPER(QPAR.LOT_NO)) LIKE ('%' || TRIM(UPPER(#{lotNo})) || '%')
AND TRIM(UPPER(QPAR.BATCH_NO)) LIKE ('%' || TRIM(UPPER(#{batchNo})) || '%')
AND TRIM(UPPER(QPAP.ANS_NO)) LIKE ('%' || TRIM(UPPER(#{ansNo})) || '%')
AND QPAP.REQ_DT BETWEEN #{searchReqDt[0]} AND #{searchReqDt[1]}
ORDER BY QPAR.REQ_IDX

mapper.update:
UPDATE QT_PITM_ANS_INFO SET
    RPT_SPCC = #{rptSpcc}
    , ARPT_SPCC = #{arptSpcc}
WHERE PLNT_CD = #{plntCd} AND ANS_IDX = #{ansIdx}
