mapper.select:
SELECT QPAI.PLNT_CD, QPAI.ANS_IDX, QPAP.ANS_NO, QPAP.ANS_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.ANS_TYP) AS ANS_TYP_NM, QPAP.REQ_DT, QPAP.RCP_DT, QPAP.CLLT_DT, QPAP.CLLT_UID, (SELECT UN.USER_NM FROM SY_USER UN WHERE UN.PLNT_CD = QPAP.PLNT_CD AND UN.USER_ID = QPAP.CLLT_UID) AS CLLT_UID_NM, QPAP.ASS_DT, QPAP.ANS_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.ANS_PROC_CD) AS ANS_PROC_CD_NM, QPAR.LOT_NO, QPAR.BATCH_NO, QPAR.MAK_DT, QPAR.MTR_CD, QPAR.USE_LMT, QPAR.ETR_QTY, QPAR.INP_UNIT, QPAR.ETR_DT, QPAR.SPL_CD, QPAR.SPL_NM, QPAR.CSM_NM, QPAR.SPL_LOT_NO, QPI.PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM, QPI.PITM_NM, QPI.PITM_CD, DECODE(QPI.PITM_TYP, #{pitmCdForDecode}, QPAR.SPL_LOT_NO, QPAR.LOT_NO) AS CRT_NO, QPAI.CRG_DPT_CD, (SELECT DPT_NM FROM SY_DPT WHERE DPT_CD = QPAI.CRG_DPT_CD AND PLNT_CD = QPAI.PLNT_CD) AS CRG_DPT_CD_NM, QPAI.SPCM_NO, QPAI.ANS_FILE_IDX, QPAI.CLLT_MTD, QPAI.CLLT_MCN, QPAI.CLLT_PLA, QPAI.SMP_STRG_MTD, QPAI.SMP_VOL_UNIT, QPAI.SMP_VOL_ANS, QPAI.SMP_VOL_ETC, QPAI.SMP_VOL_STRG, QPAI.SMP_VOL_ADD, QPAI.SMP_VOL_MNG, QPAI.ANS_NEW_YN, QPAI.MIC_YN, QPAI.RTST_YN, QPAI.ANS_ORG_REQ_IDX, QPAI.EMG_YN, QPAI.ARPT_RPT_IDX, QPAI.PKG_MTR_SPEC, QPAI.RMTR_SPEC, QPAI.RMTR_CFMUL, QPAI.RPT_SPCC, QPAI.ARPT_SPCC, QPAI.FTN_YN, QPAI.QDRUG_YN, QPAI.PITM_SPCC, QPAI.NON_CFM_PROC_CD, QPAI.NON_CFM_APR_REQ_IDX, QPAI.NON_CFM_REA, QPAI.NON_CFM_REV, QPAI.NON_CFM_REF, QPAI.NON_CFM_ETC, QPAI.REO_PREV_APR_REQ_IDX, QPAI.REO_PREV_RPY_CTT, QPAI.ISP_PDT_PFA_IDX, QPAI.ISP_PHS_PFA_IDX, QPAI.SMP_LABEL_PRT_IDX, (NVL(QPAI.SMP_VOL_ANS, 0) + NVL(QPAI.SMP_VOL_ETC, 0) + NVL(QPAI.SMP_VOL_STRG, 0)) AS SMP_VOL_TOT, QSLP.LABEL_PRT_IDX, QSLP.PRT_SEQ, QSLP.LABEL_RPT_IDX, QSLP.LABEL_PRT_PROC, QSLP.LABEL_RE_PRT_RJT_UID, (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QSLP.LABEL_RE_PRT_RJT_UID) AS LABEL_RE_PRT_RJT_UID_NM, QSLP.LABEL_RE_PRT_RJT_DS, TO_CHAR(QSLP.LABEL_RE_PRT_RJT_DS, 'YYYY-MM-DD') AS LABEL_RE_PRT_RJT_DT, QSLP.LABEL_RE_PRT_RJT_REA, QSLP.PRT_UID, (SELECT SU.USER_NM FROM SY_USER SU WHERE SU.PLNT_CD = QPAP.PLNT_CD AND SU.USER_ID = QSLP.PRT_UID) AS PRT_UID_NM, QSLP.PRT_DS, TO_CHAR(QSLP.PRT_DS, 'YYYY-MM-DD') PRT_DT, SRM.RPT_DIV, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = SRM.RPT_DIV) AS RPT_DIV_NM FROM QT_PITM_ANS_INFO QPAI INNER JOIN QT_PITM_ANS_PROC QPAP ON QPAI.PLNT_CD = QPAP.PLNT_CD AND QPAI.ANS_IDX = QPAP.ANS_IDX INNER JOIN QT_PITM_ANS_REQ QPAR ON QPAP.PLNT_CD = QPAR.PLNT_CD AND QPAP.REQ_IDX = QPAR.REQ_IDX INNER JOIN QM_PITM_SPEC QPS ON QPAP.PLNT_CD = QPS.PLNT_CD AND QPAP.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX INNER JOIN QM_PITM_INFO QPI ON QPS.PLNT_CD = QPI.PLNT_CD AND QPS.PITM_CD = QPI.PITM_CD AND QPS.PITM_VER = QPI.PITM_VER INNER JOIN QS_SMP_LABEL_PRT QSLP ON QPAI.SMP_LABEL_PRT_IDX = QSLP.LABEL_PRT_IDX AND QSLP.LABEL_PRT_PROC IN
#{item}
INNER JOIN SY_RPT_MST SRM ON QSLP.LABEL_RPT_IDX = SRM.RPT_IDX WHERE 1 = 1 AND QPAI.PLNT_CD = #{plntCd} AND QSLP.PRT_SEQ = (SELECT MAX(A.PRT_SEQ) FROM QS_SMP_LABEL_PRT A WHERE A.LABEL_PRT_IDX = QSLP.LABEL_PRT_IDX)
AND QPI.PITM_TYP = #{pitmTyp}
AND SRM.RPT_DIV = #{rptDiv}
AND QPAP.ANS_TYP = #{ansTyp}
AND UPPER(QPI.PITM_CD) LIKE UPPER('%' || #{pitmCd} || '%')
AND QPAP.ANS_PROC_CD = #{ansProcCd}
AND UPPER(QPAP.ANS_NO) LIKE UPPER('%' || #{ansNo} || '%')
AND UPPER(QPAR.BATCH_NO) LIKE UPPER('%' || #{batchNo} || '%')
AND UPPER(QPAR.LOT_NO) LIKE UPPER('%' || #{lotNo} || '%')
AND QPAP.RCP_DT BETWEEN #{searchRcpDt[0]} AND #{searchRcpDt[1]}
ORDER BY QPAI.ANS_IDX DESC

SELECT SCD.DTL_CD AS RPT_DIV , SCD.DTL_NM AS RPT_DIV_NM , BASE.PLNT_CD , BASE.ANS_IDX , BASE.PRT_UID , (SELECT UN.USER_NM FROM SY_USER UN WHERE UN.PLNT_CD = BASE.PLNT_CD AND UN.USER_ID = BASE.PRT_UID) AS PRT_UID_NM , TO_CHAR(BASE.PRT_DS, 'YYYY-MM-DD') AS PRT_DT , BASE.LABEL_RE_PRT_RJT_UID , (SELECT UN.USER_NM FROM SY_USER UN WHERE UN.PLNT_CD = BASE.PLNT_CD AND UN.USER_ID = BASE.LABEL_RE_PRT_RJT_UID) AS LABEL_RE_PRT_RJT_UID_NM , TO_CHAR(BASE.LABEL_RE_PRT_RJT_DS, 'YYYY-MM-DD') AS LABEL_RE_PRT_RJT_DT , BASE.LABEL_RE_PRT_RJT_REA FROM SY_CD_DTL SCD LEFT JOIN ( SELECT SRM.RPT_DIV , QPAI.PLNT_CD , QPAI.ANS_IDX , QSLP.LABEL_RE_PRT_RJT_UID , QSLP.LABEL_RE_PRT_RJT_DS , QSLP.LABEL_RE_PRT_RJT_REA , QSLP.PRT_UID , QSLP.PRT_DS FROM QT_PITM_ANS_INFO QPAI INNER JOIN QS_SMP_LABEL_PRT QSLP ON QPAI.SMP_LABEL_PRT_IDX = QSLP.LABEL_PRT_IDX AND (QSLP.PRT_SEQ = (SELECT MAX(A.PRT_SEQ) FROM QS_SMP_LABEL_PRT A WHERE A.LABEL_PRT_IDX = QSLP.LABEL_PRT_IDX)) INNER JOIN SY_RPT_MST SRM ON QSLP.LABEL_RPT_IDX = SRM.RPT_IDX WHERE 1 = 1 AND QPAI.PLNT_CD = #{plntCd} AND QPAI.ANS_IDX = #{ansIdx} ) BASE ON SCD.DTL_CD = BASE.RPT_DIV WHERE 1 = 1 AND SCD.HIR_CD = 'S024'
AND SCD.DTL_CD = #{rptDiv}
AND UPPER((SELECT UN.USER_NM FROM SY_USER UN WHERE UN.PLNT_CD = BASE.PLNT_CD AND UN.USER_ID = BASE.PRT_UID)) LIKE UPPER('%' || #{prtUidNm} || '%')
AND TO_DATE(BASE.PRT_DS) BETWEEN #{searchPrtDt[0]} AND #{searchPrtDt[1]}
ORDER BY SCD.DTL_CD_ORD

mapper.update:
UPDATE QS_SMP_LABEL_PRT SET
    LABEL_PRT_PROC = #{labelPrtProc},
    LABEL_RE_PRT_APR_IDX = #{labelRePrtAprIdx}
WHERE 1 = 1
    AND LABEL_PRT_IDX = #{labelPrtIdx}
    AND PRT_SEQ = #{prtSeq}

UPDATE SY_RPT_MST SET
    RPT_DIV = #{rptDiv}
WHERE 1 = 1
    AND RPT_IDX = #{rptIdx}

UPDATE QS_SMP_LABEL_PRT SET
    LABEL_PRT_PROC = #{labelPrtProc}
WHERE 1 = 1
    AND LABEL_PRT_IDX = #{labelPrtIdx}
    AND PRT_SEQ = #{prtSeq}
