mapper.select:
SELECT A.PLNT_CD, A.RITM_ETR_IDX, (SELECT TREE_CD FROM SY_CD_TREE WHERE TREE_CD = D.HIR_TREE_CD AND PLNT_CD = D.PLNT_CD) AS UPPER_RITM_TREE_CD, (SELECT LTRIM(SYS_CONNECT_BY_PATH(TREE_NM, '.'), '.') FROM SY_CD_TREE WHERE TREE_CD = D.HIR_TREE_CD START WITH HIR_TREE_CD = 'R0000000' AND PLNT_CD = D.PLNT_CD CONNECT BY PRIOR TREE_CD = HIR_TREE_CD AND PLNT_CD = D.PLNT_CD) AS UPPER_RITM_TREE_NM, A.RITM_ROOT_CD, (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = A.RITM_ROOT_CD AND PLNT_CD = A.PLNT_CD) AS RITM_ROOT_NM, A.RITM_CD, A.RITM_KN, A.RITM_EN, A.RITM_ABBR, A.RITM_CRG_UID, F.USER_ID AS RITM_CRG_UID, F.USER_NM AS RITM_CRG_NM, G.DPT_NM AS RITM_CRG_DPT_NM, A.MNG_NO, A.ETR_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.ETR_PROC_CD) AS ETR_PROC_NM, A.STRG_TERMS, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.STRG_TERMS AND PLNT_CD = A.PLNT_CD) AS STRG_TERMS_NM, A.STRG_PLA, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.STRG_PLA AND PLNT_CD = A.PLNT_CD) AS STRG_PLA_NM, A.STRG_PLA_DTL, A.RITM_UNIT_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.RITM_UNIT_CD AND PLNT_CD = A.PLNT_CD) AS RITM_UNIT_NM, A.SFY_STOK, A.ETR_QTY, A.ETR_EACH_QTY, A.ETR_TOT_QTY, A.VDR_CD, E.VDR_NM, E.VDR_CRG_NM, E.VDR_CRG_TEL, A.MAK_DT, A.MAK_NO, A.ETR_DT, A.ETR_RMK, A.EXPIR_DT_CHK, A.OPN_BEF_EXPIR_DT, A.OPN_AFT_EXPIR_TRM, A.CURR_LOT_YN, A.ETR_REQ_APR_IDX, A.SPEC, A.CAS_NO, A.FOMU, A.SHADE_YN, A.MTR_DEDUT_YN, A.POIS_YN, A.DANG_YN, A.FILE_IDX, B.STD_MOIS, B.STD_NB, B.STD_CTET_CD, B.STD_CTET_VAL, B.STD_CTET_UNIT_CD, B.STD_ASIS_CTET_NM, B.STD_ASIS_CTET_VAL, B.STD_ASIS_CTET_UNIT_CD, B.STD_RMTR_MAK_NO, B.STD_RMTR_ANS_NO, C.RITM_MNG_IDX, C.RITM_ETR_NO, C.RITM_LABEL_NO, C.MNG_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = C.MNG_PROC_CD) AS MNG_PROC_NM, C.OPN_DS, C.EXPIR_DT, C.OPN_RMK, C.DPS_REA, C.RJT_REA, A.ETR_QTY_UNIT_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE PLNT_CD = A.PLNT_CD AND DTL_CD = A.ETR_QTY_UNIT_CD) AS ETR_QTY_UNIT_NM FROM RT_RITM_ETR A JOIN RT_RITM_ETR_INFO_STD B ON A.RITM_ETR_IDX = B.RITM_ETR_IDX JOIN RT_RITM_MNG C ON B.RITM_ETR_IDX = C.RITM_ETR_IDX JOIN SY_CD_TREE D ON A.RITM_ROOT_CD = D.TREE_CD AND A.PLNT_CD = D.PLNT_CD JOIN SY_USER F ON A.RITM_CRG_UID = F.USER_ID JOIN SY_DPT G ON F.DPT_CD = G.DPT_CD AND F.PLNT_CD = G.PLNT_CD LEFT OUTER JOIN RM_RITM_VDR E ON A.VDR_CD = E.VDR_CD WHERE A.PLNT_CD = #{plntCd} AND A.ETR_PROC_CD = #{etrProcCd} AND C.MNG_PROC_CD IN
#{item}
AND D.HIR_TREE_CD = #{upperRitmTreeCd}
AND A.RITM_ROOT_CD = #{ritmTreeCd}
AND A.RITM_KN LIKE '%' || UPPER(#{ritmKn}) || '%'
AND A.ETR_DT BETWEEN #{searchEtrDt[0]} AND #{searchEtrDt[1]}
AND A.MNG_NO LIKE '%' || UPPER(#{mngNo}) || '%'
AND A.MAK_NO LIKE '%' || UPPER(#{makNo}) || '%'
AND E.VDR_NM LIKE '%' || UPPER(#{vdrNm}) || '%'
ORDER BY A.RITM_ETR_IDX, C.RITM_MNG_IDX

mapper.update:
UPDATE RT_RITM_MNG SET
    MNG_PROC_CD = #{mngProcCd},
    DPS_REQ_APR_IDX = #{dpsReqAprIdx},
    DPS_REA = #{dpsRea}
WHERE RITM_MNG_IDX = #{ritmMngIdx} AND PLNT_CD = #{plntCd}
