mapper.select:
SELECT A.PLNT_CD, A.RITM_ETR_IDX, (SELECT TREE_CD FROM SY_CD_TREE WHERE TREE_CD = E.HIR_TREE_CD) AS UPPER_RITM_TREE_CD, (SELECT LTRIM(SYS_CONNECT_BY_PATH(TREE_NM, '.'), '.') FROM SY_CD_TREE WHERE TREE_CD = E.HIR_TREE_CD START WITH HIR_TREE_CD = 'R0000000' CONNECT BY PRIOR TREE_CD = HIR_TREE_CD) AS UPPER_RITM_TREE_NM, A.RITM_ROOT_CD, (SELECT TREE_NM FROM SY_CD_TREE WHERE TREE_CD = A.RITM_ROOT_CD) AS RITM_ROOT_NM, A.RITM_CD, A.RITM_KN, A.RITM_EN, A.RITM_ABBR, A.RITM_CRG_UID, (SELECT USER_NM FROM SY_USER WHERE USER_ID = A.RITM_CRG_UID) AS RITM_CRG_NM, A.MNG_NO, A.ETR_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.ETR_PROC_CD) AS ETR_PROC_NM, A.STRG_TERMS, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.STRG_TERMS) AS STRG_TERMS_NM, A.STRG_PLA, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.STRG_PLA) AS STRG_PLA_NM, A.STRG_PLA_DTL, A.RITM_UNIT_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = A.RITM_UNIT_CD) AS RITM_UNIT_NM, A.SFY_STOK, A.ETR_QTY, A.ETR_EACH_QTY, A.ETR_TOT_QTY, A.VDR_CD, D.VDR_NM, D.VDR_CRG_NM, D.VDR_CRG_TEL, A.MAK_DT, A.MAK_NO, A.ETR_DT, A.ETR_RMK, A.EXPIR_DT_CHK, A.OPN_BEF_EXPIR_DT, A.OPN_AFT_EXPIR_TRM, A.CURR_LOT_YN, A.ETR_REQ_APR_IDX, A.RJT_UID, A.RJT_DS, A.RJT_REA, A.DEL_UID, A.DEL_DS, A.DEL_REA, A.DEL_YN, A.CRT_IP, A.CRT_DS, A.CRT_UID, A.UDT_IP, A.UDT_DS, A.UDT_UID, A.SPEC, A.CAS_NO, A.FOMU, A.SHADE_YN, A.MTR_DEDUT_YN, A.POIS_YN, A.DANG_YN, B.STD_MOIS, B.STD_NB, B.STD_CTET_CD, B.STD_CTET_VAL, B.STD_CTET_UNIT_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = B.STD_CTET_UNIT_CD) AS STD_CTET_UNIT_NM, B.STD_ASIS_CTET_NM, B.STD_ASIS_CTET_VAL, B.STD_ASIS_CTET_UNIT_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = B.STD_ASIS_CTET_UNIT_CD) AS STD_ASIS_CTET_UNIT_NM, B.STD_RMTR_MAK_NO, B.STD_RMTR_ANS_NO, C.APR_YN, A.FILE_IDX FROM RT_RITM_ETR A JOIN RT_RITM_ETR_INFO_STD B ON A.RITM_ETR_IDX = B.RITM_ETR_IDX JOIN SY_CD_TREE E ON A.RITM_ROOT_CD = E.TREE_CD LEFT OUTER JOIN SY_APR_INFO C ON A.ETR_REQ_APR_IDX = C.APR_IDX LEFT OUTER JOIN RM_RITM_VDR D ON A.VDR_CD = D.VDR_CD WHERE A.DEL_YN = 'N' AND A.PLNT_CD = #{plntCd}
AND E.HIR_TREE_CD = #{upperRitmTreeCd}
AND A.RITM_ROOT_CD = #{ritmTreeCd}
AND A.RITM_KN LIKE '%' || UPPER(#{ritmKn}) || '%'
AND A.MAK_DT BETWEEN #{searchMakDt[0]} AND #{searchMakDt[1]}
AND A.MNG_NO LIKE '%' || UPPER(#{mngNo}) || '%'
AND A.MAK_NO LIKE '%' || UPPER(#{makNo}) || '%'
AND A.ETR_REQ_APR_IDX IS NOT NULL AND C.APR_YN = 'Y'
AND A.ETR_REQ_APR_IDX IS NOT NULL AND C.APR_YN = 'N'
AND D.VDR_NM LIKE '%' || UPPER(#{vdrNm}) || '%'
ORDER BY A.RITM_ETR_IDX

mapper.update:
UPDATE RT_RITM_ETR SET
    DEL_YN = 'Y',
    DEL_UID = #{delUid},
    DEL_DS = SYSDATE,
    DEL_REA = #{delRea}
WHERE RITM_ETR_IDX = #{ritmEtrIdx} AND PLNT_CD = #{plntCd}

UPDATE RT_RITM_ETR SET
    ETR_REQ_APR_IDX = #{etrReqAprIdx},
    ETR_PROC_CD = #{etrProcCd}
WHERE RITM_ETR_IDX = #{ritmEtrIdx} AND PLNT_CD = #{plntCd}

UPDATE RT_RITM_ETR SET
    FILE_IDX = #{fileIdx}
WHERE RITM_ETR_IDX = #{ritmEtrIdx} AND PLNT_CD = #{plntCd}
