mapper.select:
SELECT SPS.PLNT_CD , SPS.PM_SPCM_IDX , SPS.PKG_MTR_DIV , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = SPS.PKG_MTR_DIV) AS PKG_MTR_DIV_NM , SPS.PM_SPCM_NO , SPS.PM_SPCM_DEL_APR_IDX , SPS.RJT_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = SPS.RJT_UID) AS RJT_UNM , SPS.RJT_DS , SPS.RJT_REA , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = SPS.CRT_UID) AS CRT_UNM , SPS.CRT_DS , SPS.PM_SPCM_PROC , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = SPS.PM_SPCM_PROC) AS PM_SPCM_PROC_NM , SPSN.DEL_APR_YN , SPSA.ACSR_SEQ , SPSA.PITM_CD , SPSA.ENM_DT , EXPIR_DT , SPSA.MUFT , SPSA.RMK , SPSA.DEL_YN , TO_CHAR(SPSA.UDT_DS, 'YYYY-MM-DD') AS UDT_DS , QPI.PITM_NM , SPSG.MAX_EXPIR_DT , (SELECT MAX(QPAP.RCP_DT) AS RCP_DT FROM QT_PITM_ANS_PROC QPAP JOIN QM_PITM_SPEC QPS ON QPAP.PLNT_CD = QPS.PLNT_CD AND QPAP.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX WHERE QPS.PITM_CD = SPSA.PITM_CD GROUP BY QPS.PITM_CD ) AS FIN_RCP_DT FROM SD_PM_SPCM SPS JOIN (SELECT SPS.PLNT_CD , SPS.PM_SPCM_IDX , MAX(SPSA.EXPIR_DT) AS MAX_EXPIR_DT FROM SD_PM_SPCM SPS JOIN SD_PM_SPCM_NO SPSN ON SPS.PM_SPCM_NO = SPSN.PM_SPCM_NO AND SPSN.DEL_APR_YN = 'N' LEFT JOIN SD_PM_SPCM_ACSR SPSA ON SPS.PLNT_CD = SPSA.PLNT_CD AND SPS.PM_SPCM_IDX = SPSA.PM_SPCM_IDX LEFT JOIN QM_PITM_INFO QPI ON SPSA.PITM_CD = QPI.PITM_CD AND SPSA.PITM_VER = QPI.PITM_VER AND SPS.PLNT_CD = QPI.PLNT_CD WHERE SPS.PLNT_CD = #{plntCd}
AND TRIM(UPPER(QPI.PITM_NM)) LIKE ('%' || TRIM(UPPER(#{pitmNm})) || '%')
AND TRIM(SPSA.PITM_CD) LIKE ('%' || #{pitmCd} || '%')
AND SPSA.ENM_DT BETWEEN #{searchEnmDt[0]} AND #{searchEnmDt[1]}
AND SPSA.EXPIR_DT BETWEEN #{searchExpirDt[0]} AND #{searchExpirDt[1]}
GROUP BY SPS.PLNT_CD, SPS.PM_SPCM_IDX ) SPSG ON SPS.PLNT_CD = SPSG.PLNT_CD AND SPS.PM_SPCM_IDX = SPSG.PM_SPCM_IDX JOIN SD_PM_SPCM_NO SPSN ON SPS.PM_SPCM_NO = SPSN.PM_SPCM_NO AND SPSN.DEL_APR_YN = 'N' LEFT JOIN SD_PM_SPCM_ACSR SPSA ON SPS.PLNT_CD = SPSA.PLNT_CD AND SPS.PM_SPCM_IDX = SPSA.PM_SPCM_IDX LEFT JOIN QM_PITM_INFO QPI ON SPSA.PITM_CD = QPI.PITM_CD AND SPSA.PITM_VER = QPI.PITM_VER AND SPS.PLNT_CD = QPI.PLNT_CD WHERE 1=1
AND SPS.PKG_MTR_DIV = #{pkgMtrDiv}
AND SPS.PM_SPCM_PROC = #{pmSpcmProc}
AND TRIM(UPPER(SPS.PM_SPCM_NO)) LIKE ('%' || TRIM(UPPER(#{pmSpcmNo})) || '%')
AND SPSN.DEL_APR_YN = #{delAprYn}
ORDER BY SPS.PM_SPCM_NO, SPS.PM_SPCM_IDX, SPSA.ACSR_SEQ

mapper.insert:

    SELECT NVL(MAX(PM_SPCM_IDX)+1,1) FROM SD_PM_SPCM WHERE PLNT_CD = #{plntCd}

INSERT INTO SD_PM_SPCM
(
    PLNT_CD
    , PM_SPCM_IDX
    , PKG_MTR_DIV
    , PM_SPCM_NO
    , PM_SPCM_PROC
) VALUES (
    #{plntCd}
    , #{pmSpcmIdx}
    , #{pkgMtrDiv}
    , #{pmSpcmNo}
    , 'S0220100'
)

mapper.update:
UPDATE SD_PM_SPCM SET
    PM_SPCM_NO = #{pmSpcmNo}
    , PKG_MTR_DIV = #{pkgMtrDiv}
WHERE PLNT_CD = #{plntCd}
AND PM_SPCM_IDX = #{pmSpcmIdx}

UPDATE SD_PM_SPCM SET
    PM_SPCM_DEL_APR_IDX = #{pmSpcmDelAprIdx}
    , PM_SPCM_PROC = 'S0220200'
WHERE PLNT_CD = #{plntCd}
AND PM_SPCM_IDX = #{pmSpcmIdx}

mapper.insert:

    SELECT NVL(MAX(ACSR_SEQ)+1,1) FROM SD_PM_SPCM_ACSR WHERE PLNT_CD = #{plntCd} AND PM_SPCM_IDX = #{pmSpcmIdx}

INSERT INTO SD_PM_SPCM_ACSR(
    PLNT_CD
    , PM_SPCM_IDX
    , ACSR_SEQ
    , PITM_CD
    , PITM_VER
    , ENM_DT
    , EXPIR_DT
    , APR_UID
    , MUFT
    , RMK
    , DEL_YN
) VALUES (
    #{plntCd}
    , #{pmSpcmIdx}
    , #{acsrSeq}
    , #{pitmCd}
    , #{pitmVer}
    , #{enmDt}
    , #{expirDt}
    , #{aprUid}
    , #{muft}
    , #{rmk}
    , 'N'
)

mapper.update:
UPDATE SD_PM_SPCM_ACSR SET
    ENM_DT = #{enmDt}
    , EXPIR_DT = #{expirDt}
    , MUFT = #{muft}
    , RMK = #{rmk}
WHERE PLNT_CD = #{plntCd}
AND PM_SPCM_IDX = #{pmSpcmIdx}
AND ACSR_SEQ = #{acsrSeq}

UPDATE SD_PM_SPCM_ACSR SET
    DEL_YN = 'Y'
WHERE PLNT_CD = #{plntCd}
AND PM_SPCM_IDX = #{pmSpcmIdx}
AND ACSR_SEQ = #{acsrSeq}
