mapper.select:
SELECT SPS.PLNT_CD , SPS.PS_SPCM_IDX , SPS.PITM_VER , SPS.PITM_CD , SPS.PDT_NO , SPS.ENM_DT , SPS.EXPIR_DT , SPS.APR_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = SPS.APR_UID) APR_UNM , SPS.MUFT , SPS.RMK , SPS.USE_YN , SPS.SPCM_NO , QPI.PITM_NM , QPI.PITM_EN , QPI.PITM_TYP , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM FROM SD_PS_SPCM SPS LEFT JOIN QM_PITM_INFO QPI ON SPS.PITM_CD = QPI.PITM_CD AND SPS.PITM_VER = QPI.PITM_VER AND SPS.PLNT_CD = QPI.PLNT_CD WHERE SPS.PLNT_CD = #{plntCd}
AND TRIM(UPPER(QPI.PITM_NM)) LIKE ('%' || TRIM(UPPER(#{pitmNm})) || '%')
AND TRIM(UPPER(SPS.PITM_CD)) LIKE ('%' || TRIM(UPPER(#{pitmCd})) || '%')
AND SPS.USE_YN = #{useYn}
AND SPS.ENM_DT BETWEEN #{searchEnmDt[0]} AND #{searchEnmDt[1]}
AND SPS.EXPIR_DT BETWEEN #{searchExpirDt[0]} AND #{searchExpirDt[1]}
ORDER BY SPS.SPCM_NO, SPS.PS_SPCM_IDX

mapper.insert:

    SELECT NVL(MAX(PS_SPCM_IDX)+1,1) FROM SD_PS_SPCM WHERE PLNT_CD = #{plntCd}

INSERT INTO SD_PS_SPCM(
    PLNT_CD
    , PS_SPCM_IDX
    , PITM_VER
    , PITM_CD
    , PDT_NO
    , ENM_DT
    , EXPIR_DT
    , APR_UID
    , MUFT
    , RMK
    , SPCM_NO
) VALUES (
    #{plntCd}
    , #{psSpcmIdx}
    , #{pitmVer}
    , #{pitmCd}
    , '1'
    , #{enmDt}
    , TO_DATE(#{expirDt}, 'YYYY-MM-DD HH24:MI:SS')
    , #{aprUid}
    , #{muft}
    , #{rmk}
    , (SELECT 'S' || LPAD(NVL(MAX(SUBSTR(SPCM_NO, -5))+1,1),5,0) FROM SD_PS_SPCM WHERE PLNT_CD = #{plntCd})
)

mapper.update:
UPDATE SD_PS_SPCM SET
    PDT_NO = (SELECT NVL(MAX(PDT_NO)+1,1) FROM SD_PS_SPCM WHERE PLNT_CD = #{plntCd} AND PS_SPCM_IDX = #{psSpcmIdx})
    , ENM_DT = #{enmDt}
    , EXPIR_DT = TO_DATE(#{expirDt}, 'YYYY-MM-DD HH24:MI:SS')
    , MUFT = #{muft}
    , RMK = #{rmk}
    , USE_YN = #{useYn}
WHERE PLNT_CD = #{plntCd}
AND PS_SPCM_IDX = #{psSpcmIdx}
