mapper.select:
SELECT SS.PLNT_CD , SS.SPCM_IDX , SS.PITM_VER , SS.PITM_CD , SS.REV_NO , SS.ENM_DT , TO_CHAR(SS.EXPIR_DT, 'YYYY-MM-DD') AS EXPIR_DT , SS.APR_UID , (SELECT USER_NM FROM SY_USER SU WHERE SU.USER_ID = SS.APR_UID) APR_UNM , SS.MUFT , SS.RMK , SS.USE_YN , SS.SPCM_NO , QPI.PITM_NM , QPI.PITM_EN , QPI.PITM_TYP , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM FROM SD_SPCM SS LEFT JOIN QM_PITM_INFO QPI ON SS.PITM_CD = QPI.PITM_CD AND SS.PITM_VER = QPI.PITM_VER AND SS.PLNT_CD = QPI.PLNT_CD WHERE SS.PLNT_CD = #{plntCd}
AND TRIM(UPPER(QPI.PITM_NM)) LIKE ('%' || TRIM(UPPER(#{pitmNm})) || '%')
AND TRIM(UPPER(SS.PITM_CD)) LIKE ('%' || TRIM(UPPER(#{pitmCd})) || '%')
AND TRIM(UPPER(SS.REV_NO)) LIKE ('%' || TRIM(UPPER(#{revNo})) || '%')
AND SS.USE_YN = #{useYn}
AND SS.ENM_DT BETWEEN #{searchEnmDt[0]} AND #{searchEnmDt[1]}
AND SS.EXPIR_DT BETWEEN #{searchExpirDt[0]} AND #{searchExpirDt[1]}
ORDER BY SS.SPCM_NO, SS.SPCM_IDX

mapper.insert:

    SELECT NVL(MAX(SPCM_IDX)+1,1) FROM SD_SPCM WHERE PLNT_CD = #{plntCd}

INSERT INTO SD_SPCM(
    PLNT_CD
    , SPCM_IDX
    , PITM_VER
    , PITM_CD
    , REV_NO
    , ENM_DT
    , EXPIR_DT
    , APR_UID
    , MUFT
    , RMK
    , USE_YN
    , SPCM_NO
) VALUES (
    #{plntCd}
    , #{spcmIdx}
    , #{pitmVer}
    , #{pitmCd}
    , '1'
    , #{enmDt}
    , TO_DATE(#{expirDt}, 'YYYY-MM-DD HH24:MI:SS')
    , #{aprUid}
    , #{muft}
    , #{rmk}
    , #{useYn}
    , (SELECT 'S' || LPAD(NVL(MAX(SUBSTR(SPCM_NO, -5))+1,1),5,0) FROM SD_SPCM WHERE PLNT_CD = #{plntCd})
)

mapper.update:
UPDATE SD_SPCM SET
    REV_NO = (SELECT NVL(MAX(REV_NO)+1,1) FROM SD_SPCM WHERE PLNT_CD = #{plntCd} AND SPCM_IDX = #{spcmIdx})
    , EXPIR_DT = TO_DATE(#{expirDt}, 'YYYY-MM-DD HH24:MI:SS')
    , MUFT = #{muft}
    , RMK = #{rmk}
    , USE_YN = #{useYn}
WHERE PLNT_CD = #{plntCd}
AND SPCM_IDX = #{spcmIdx}

mapper.insert:

    SELECT NVL(MAX(HIS_SEQ)+1,1) FROM SD_SPCM_HIS WHERE PLNT_CD = #{plntCd} AND SPCM_IDX = #{spcmIdx}

INSERT INTO SD_SPCM_HIS(
    PLNT_CD
    , SPCM_IDX
    , HIS_SEQ
    , HIS_REV_NO
    , CHG_REA
    , UDT_HIS
) VALUES (
    #{plntCd}
    , #{spcmIdx}
    , #{hisSeq}
    , #{revNo}
    , #{chgRea}
    , #{udtHis}
)
