mapper.update:
UPDATE ST_SBT_PLN
SET SBT_ANS_PROC = #{sbtAnsProc}
WHERE 1 = 1
    AND PLNT_CD = #{plntCd}
    AND SBT_PLN_IDX = #{sbtPlnIdx}

UPDATE ST_SBT_PLN
    SET SBT_ANS_PROC = #{sbtAnsProc},
        PLN_RJT_UID = #{loginUserUid},
        PLN_RJT_DS = SYSDATE,
        PLN_RJT_REA = #{plnRjtRea}
WHERE 1 = 1
    AND PLNT_CD = #{plntCd}
    AND SBT_PLN_IDX = #{sbtPlnIdx}
