mapper.update:
UPDATE QS_SMP_USE
SET SMP_USE_PROC = 'S0280200',
    SMP_USE_APR_IDX = #{smpUseAprIdx}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}

UPDATE QS_SMP_USE
SET SMP_USE_PROC = 'S0280400',
    SMP_USE_APR_IDX = #{smpUseAprIdx}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}

UPDATE QS_SMP_USE
SET USE_DT      = #{useDt},
    USE_SMP_VOL = #{useSmpVol},
    USE_PPS     = #{usePps}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}

UPDATE QS_SMP_USE
SET DEL_YN  = 'Y',
    DEL_REA = #{delRea}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}
