mapper.select:
SELECT SA.PLNT_CD, SA.ATH_CD, SA.ATH_NM, SA.ATHDESC, SA.ORD, SA.USE_YN, SA.ANS_YN, SA.REVW_YN, SA.APR_YN FROM SY_ATH SA WHERE SA.PLNT_CD = #{plntCd}
AND ATH_NM LIKE '%' || #{athNm} || '%'
AND USE_YN = #{useYn}
ORDER BY SA.ORD

mapper.update:
UPDATE SY_ATH
SET ATH_NM  = #{athNm},
    ATHDESC = #{athdesc},
    ORD     = #{ord},
    USE_YN  = #{useYn},
    ANS_YN  = #{ansYn},
    REVW_YN = #{revwYn},
    APR_YN  = #{aprYn}
WHERE PLNT_CD = #{plntCd}
  AND ATH_CD = #{athCd}

mapper.insert:

    SELECT 'A'||LPAD(NVL(MAX(SUBSTR(ATH_CD, -4))+1,1),4,0) FROM SY_ATH WHERE PLNT_CD = #{plntCd}

INSERT INTO SY_ATH (PLNT_CD, ATH_CD, ATH_NM, ATHDESC, ORD, USE_YN, ANS_YN, REVW_YN, APR_YN)
VALUES (#{plntCd}, #{athCd}, #{athNm}, #{athdesc}, #{ord}, #{useYn}, #{ansYn}, #{revwYn}, #{aprYn})

mapper.delete:
DELETE SY_ATH_GP
WHERE PLNT_CD = #{plntCd}
  AND ATH_CD = #{athCd}
  AND MENU_CD = #{menuCd}

mapper.select:
SELECT MENU_CD
FROM SY_ATH_GP
WHERE ATH_CD = #{athCd}
  AND PLNT_CD = #{plntCd}
ORDER BY MENU_CD
