mapper.select:
SELECT PLNT_CD, HIR_CD, DTL_CD, DTL_NM, DTL_EN, DTL_CD_ORD, USE_YN, DTL_RMK, EXT_CD1, EXT_CD2, EXT_CD3 FROM SY_CD_DTL WHERE HIR_CD = #{hirCd}
AND PLNT_CD = #{plntCd}
AND UPPER(DTL_NM) LIKE ('%' || TRIM(UPPER(#{dtlNm})) || '%')
AND USE_YN = #{useYn}
ORDER BY DTL_CD_ORD

mapper.update:
UPDATE SY_CD_HIR SET
    HIR_CD_NM = #{hirCdNm},
    HIR_DESC = #{hirDesc},
    USE_YN = #{useYn},
    HIR_CD_ORD = #{hirCdOrd}
WHERE HIR_CD = #{hirCd} AND PLNT_CD = #{plntCd}
