mapper.update:
UPDATE QS_SMP_MNG
SET SMP_DPS_PROC    = #{smpDpsProc},
    SMP_DPS_APR_IDX = #{smpDpsAprIdx},
    DPS_REA         = #{dpsRea}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}

UPDATE QS_SMP_MNG
SET SMP_DPS_PROC    = #{smpDpsProc},
    SMP_DPS_APR_IDX = #{smpDpsAprIdx},
    DPS_CANL_REA    = #{dpsCanlRea}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
