mapper.select:
SELECT QSM.PLNT_CD, QSM.SMP_MNG_IDX, QSM.SMP_DPS_PROC, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.SMP_DPS_PROC) AS SMP_DPS_NM, QSM.PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.PITM_TYP) AS PITM_TYP_NM, QSM.PITM_CD, QSM.PITM_NM, QSM.ANS_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.ANS_TYP) AS ANS_TYP_NM, QSM.ANS_IDX, QSM.SMP_DIV_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.SMP_DIV_CD) AS SMP_DIV_NM, QSM.MNG_SMP_VOL, QSM.SMP_STRG_MTD, (SELECT DTL_NM FROM SY_CD_DTL WHERE PLNT_CD = #{plntCd} AND DTL_CD = QSM.SMP_STRG_MTD) AS SMP_STRG_MTD_NM, QSM.SMP_VOL_UNIT, QSM.LOT_NO, QSM.BATCH_NO, QSM.INP_UNIT, QSM.MAK_DT, QSM.STRG_LMT, QSM.USE_LMT, QSM.IRG_YN, QSM.SMP_RMK, QSM.DPS_EXP_DT, QSM.DPS_FIX_DT, QSM.SMP_ETR_DT, QPAI.ANS_IDX AS ANS_IDX, QPAP.REQ_IDX, QPAP.ANS_PROC_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QPAP.ANS_PROC_CD) AS ANS_PROC_NM, QPAP.ANS_NO, QPAP.RCP_DT FROM QS_SMP_MNG QSM LEFT JOIN QT_PITM_ANS_INFO QPAI ON QSM.PLNT_CD = QPAI.PLNT_CD AND QSM.ANS_IDX = QPAI.ANS_IDX LEFT JOIN QT_PITM_ANS_PROC QPAP on QPAI.PLNT_CD = QPAP.PLNT_CD AND QPAI.ANS_IDX = QPAP.ANS_IDX WHERE QSM.PLNT_CD = #{plntCd} AND QSM.DEL_YN = 'N'
AND QSM.SMP_DIV_CD = #{smpDivCd}
AND QSM.SMP_DPS_PROC = #{smpDpsProc}
AND QSM.ANS_TYP = #{ansTyp}
AND QSM.PITM_TYP = #{pitmTyp}
AND QSM.LOT_NO LIKE '%' || UPPER(#{lotNo}) '%'
AND QSM.BATCH_NO LIKE '%' || UPPER(#{batchNo}) '%'
AND QPAP.ANS_NO LIKE '%' || UPPER(#{lotNo}) '%'
ORDER BY QSM.SMP_MNG_IDX DESC

mapper.update:
UPDATE QS_SMP_MNG
SET UDT_IP     = #{udtIp},
    UDT_UID    = #{udtUid},
    UDT_DS     = SYSDATE,
    DPS_EXP_DT = #{dpsExpDt},
    SMP_RMK    = #{smpRmk}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}

UPDATE QS_SMP_MNG
SET DEL_YN = 'Y'
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}

mapper.select:
SELECT QPAP.PLNT_CD, QPAP.ANS_NO, QPAP.ANS_IDX, QSM.SMP_DIV_CD, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.SMP_DIV_CD) AS SMP_DIV_NM, QSM.PITM_CD, QSM.PITM_NM, QSM.LOT_NO, QSM.BATCH_NO, QSM.USE_LMT, QSM.MAK_DT, QSM.MNG_SMP_VOL, QSM.PITM_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.PITM_TYP) AS PITM_TYP_NM, QSM.INP_UNIT, QSM.ANS_TYP, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.ANS_TYP) AS ANS_TYP_NM, QPAP.REQ_DT, QPAP.RCP_DT, (SELECT DTL_NM FROM SY_CD_DTL WHERE DTL_CD = QSM.SMP_DPS_PROC) AS SMP_DPS_NM, QSM.STRG_LMT, QSM.IRG_YN, (SELECT DTL_NM FROM SY_CD_DTL WHERE PLNT_CD = #{plntCd} AND DTL_CD = QSM.SMP_STRG_MTD) AS SMP_STRG_MTD_NM, QSM.SMP_ETR_DT FROM QT_PITM_ANS_PROC QPAP LEFT JOIN QT_PITM_ANS_INFO QPAI ON QPAP.PLNT_CD = QPAI.PLNT_CD AND QPAP.ANS_IDX = QPAI.ANS_IDX LEFT JOIN QS_SMP_MNG QSM ON QPAI.PLNT_CD = QSM.PLNT_CD AND QPAI.ANS_IDX = QSM.ANS_IDX WHERE QPAP.PLNT_CD = #{plntCd} AND QSM.SMP_DPS_PROC = 'S0270300'
AND QSM.SMP_DIV_CD = #{smpDivCd}
AND QSM.PITM_TYP = #{pitmTyp}
AND QSM.ANS_TYP = #{ansTyp}
AND QSM.PITM_NM LIKE '%' || UPPER(#{pitmNm}) || '%'
AND QPAP.ANS_NO LIKE '%' || UPPER(#{ansNo}) || '%'
AND QSM.BATCH_NO LIKE '%' || UPPER(#{batchNo}) || '%'
AND QSM.LOT_NO LIKE '%' || UPPER(#{lotNo}) || '%'
ORDER BY QPAP.REQ_DT DESC
