mapper.update:
UPDATE QS_SMP_USE
SET SMP_USE_PROC = #{smpUseProc}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}

UPDATE QS_SMP_USE
SET SMP_USE_PROC = #{smpUseProc},
    RJT_UID      = #{rjtUid},
    RJT_DS       = SYSDATE,
    RJT_REA      = #{rjtRea}
WHERE PLNT_CD = #{plntCd}
  AND SMP_MNG_IDX = #{smpMngIdx}
  AND USE_SEQ = #{useSeq}
