    , BATCH_NO
    , INP_UNIT
    , MAK_DT
    , STRG_LMT
    , USE_LMT
    , IRG_YN
    , SMP_ETR_DT
    , DEL_YN
    , DPS_YN
) VALUES (
    #{plntCd}
    , #{smpMngIdx}
    , 'S0270300'
    , #{pitmTyp}
    , #{pitmCd}
    , #{pitmNm}
    , #{ansTyp}
    , #{ansIdx}
    , #{smpDivCd}
    , #{mngSmpVol}
    , #{smpStrgMtd}
    , #{lotNo}
    , #{batchNo}
    , #{inpUnit}
    , #{makDt}
    , #{strgLmt}
    , #{useLmt}
    , 'N'
