mapper.select:
SELECT BATCH_NO , PHS_ORDER_NO , PDT_ORDER_NO , MAK_NO , IF_INFO_IDX AS FILE_IDX , SEQ AS FILE_SRLNO , FILE_NAME AS NAME , FILE_ID , FILE_DATA AS SRC , CRT_DS , IF_INFO_IDX FROM MS_SRM_REO_PREV WHERE 1=1 AND DEL_YN = 'N' AND BATCH_NO = #{batchNo}
AND PHS_ORDER_NO = #{phsOrderNo}
AND MAK_NO = #{lotNo}

mapper.update:
UPDATE MS_SRM_REO_PREV SET
    DEL_YN = 'Y'
WHERE BATCH_NO = #{batchNo}
AND SEQ = #{fileSrlno}

mapper.select:
SELECT NVL(MAX(SEQ), 0) + 1 AS SEQ
FROM MS_SRM_REO_PREV
WHERE BATCH_NO = #{batchNo}

SELECT BATCH_NO , RPT_DIV , PHS_ORDER_NO , PDT_ORDER_NO , MAK_NO , IF_INFO_IDX AS FILE_IDX , SEQ AS FILE_SRLNO , FILE_NAME AS NAME , FILE_ID , FILE_DATA AS SRC , CRT_DS , IF_INFO_IDX FROM MS_SRM_RPT WHERE 1=1 AND BATCH_NO = #{batchNo}
AND PHS_ORDER_NO = #{phsOrderNo}
AND MAK_NO = #{lotNo}
