mapper.select:
SELECT NVL(MAX(ANS_IDX), 0) + 1 AS ANS_IDX
FROM QT_PITM_ANS_PROC
WHERE PLNT_CD = #{plntCd}

mapper.insert:
INSERT INTO QT_PITM_ANS_PROC(
    PLNT_CD
    , ANS_IDX
    , REQ_IDX
    , PITM_SPEC_IDX
    , ANS_PROC_CD
    , ANS_TYP
    , REQ_DT
    , ANS_EDT
    , ANS_NO
    , RCP_DT
    , RCP_DS
    , RCP_DPT_CD
    , RCP_UID
    , RCP_RMK
    , CPL_RQM_DT
) VALUES (
    #{plntCd}
    , #{ansIdx}
    , #{reqIdx}
    , #{pitmSpecIdx}
    , #{ansProcCd}
    , DECODE(#{addCol1}, 'A', 'S0230001', 'B', 'S0230002', 'S0230003')
    , #{ispReqDt}
    , #{ispReqDt}
    , FN_GET_ANS_NO('T', #{pitmTyp}, #{ansIdx}, #{plntCd})
    , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , SYSDATE
    , #{rcpDptCd}
    , #{rcpUid}
    , #{rcpRmk}
    , #{cplRqmDt}
)

mapper.update:
UPDATE QT_PITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , ANS_NO = #{ansNo}
    , RCP_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , RCP_DS = SYSDATE
    , RCP_DPT_CD = #{rcpDptCd}
    , RCP_UID = #{rcpUid}
    , RCP_RMK = #{rcpRmk}
    , CPL_RQM_DT = #{cplRqmDt}
WHERE ANS_IDX = #{ansIdx}
AND PLNT_CD = #{plntCd}

mapper.insert:
INSERT INTO QT_PITM_ANS_VRIB (
    PLNT_CD
    , MITM_REQ_IDX8
    , RST_SEQ
    , VRIB_SEQ
)
SELECT
    #{plntCd}
    , #{ansIdx}
    , RST_SEQ
    , VRIB_SEQ
FROM QM_PITM_SPEC_VRIB
WHERE PLNT_CD = #{plntCd}
AND AITM_SPEC_IDX = #{aitmSpecIdx}
