mapper.select:
SELECT QPAR.PLNT_CD , QPAR.REQ_IDX , QPAR.ISP_REQ_NO , QPAR.ISP_REQ_DT , QPAR.MTR_CD , QPAR.MTR_NM , QPAR.BATCH_NO , QPAR.ETR_QTY , QPAR.INP_UNIT , QPAR.SAVE_PLA , QPAR.ETR_DT , QPAR.PHS_ORDER_TYP , QPAR.PHS_ORDER_NO , QPAR.PHS_ORDER_ITM , QPAR.ITM_CTG , QPAR.PHS_UNIT_PRE , QPAR.AMT_UNIT , QPAR.CURR_CD , QPAR.AMT_LOCCURR , QPAR.PHS_ORDER_QTY , QPAR.PHS_ORDER_UNIT , QPAR.SPL_CD , QPAR.SPL_NM , QPAR.PHS_CRG_NM , QPAR.DLV_YN , QPAR.VDR_CTRT_DT , QPAR.VDR_RSV_TM , QPAR.VDR_RPT_RCP_CRST , QPAR.LOT_NO , QPAR.REP_BOM_NO , QPAR.SPL_LOT_NO , QPAR.MAK_DT , QPAR.STRG_LMT , QPAR.USE_LMT , QPAR.PDT_ORDER_TYP , QPAR.PDT_ORDER_NO , QPAR.ITN_PRS_COMP_CD , QPAR.ITN_PRS_COMP_NM , QPAR.MAK_EQP , QPAR.WRK_NM , QPAR.MTR_DOC_NO , QPAR.MTR_DOC_YR , QPAR.MTR_DOC_ITM , QPAR.CSM_BP_CD , QPAR.CSM_NM , QPAR.ADD_COL1 , DECODE(QPAR.ADD_COL1, 'A', '시험(구매)', 'B', '시험(생산)', '시험(수동)') AS ADD_COL1_NM , QPAR.ADD_COL2 , QPAR.ADD_COL3 , QP.PITM_CD , QPI.PITM_NM , QPI.PITM_EN , QPI.PITM_TYP , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPI.PITM_TYP) AS PITM_TYP_NM , QPI.PKG_MTR_SPEC , QPS.AITM_SPEC_IDX , QP.PITM_VER , QPIS.LAB_NO , QPIS.BRD_ABBR , QPIS.NOM_MTR , QPIS.NOM_UNIT , QPIS.ETR_CTN_QTY , QPIS.RMTR_SPEC , QPIS.NBR , QPIS.FTN_YN , QPIS.PCS01 , QPIS.PCS02 , QPIS.PCS03 , QPIS.PCS04 , QPIS.USE_TRM , QPIS.OTC_PRD , QPIS.DMS_EPT_YN , QPIS.PEAR_DIV , QPIS.MKR_VOL , QPIS.MKR_VOL_UNIT , QPIS.ETN_ANS_REQ , QPIS.CTRPT_NO , QPIS.PER_NO , QPIS.CHAG_VOL , QPIS.CHAG_VOL_UNIT , QPIS.QDRUG_YN , QPIS.RMTR_CFMUL , DECODE(QPIS.DIO_YN, 'Y', '대상', '대상아님') AS DIO_YN , DECODE(QPIS.PRB_IN_YN, 'Y', '포함됨', '포함안됨') AS PRB_IN_YN , DECODE(QPIS.PRB_FE_YN, 'Y', '대상', '대상아님') AS PRB_FE_YN , DECODE(QPIS.PNX_FE_YN, 'Y', '대상', '대상아님') AS PNX_FE_YN , QPAP.ANS_IDX , QPAP.ANS_NO , QPAP.ANS_TYP , (SELECT DTL_NM FROM SY_CD_DTL SCD WHERE SCD.DTL_CD = QPAP.ANS_TYP) AS ANS_TYP_NM , QPAP.ANS_EDT FROM QT_PITM_ANS_REQ QPAR LEFT JOIN QT_PITM_ANS_PROC QPAP ON QPAR.REQ_IDX = QPAP.REQ_IDX AND QPAR.PLNT_CD = QPAP.PLNT_CD LEFT JOIN QT_PITM_ANS_INFO QPAI ON QPAP.ANS_IDX = QPAI.ANS_IDX AND QPAP.PLNT_CD = QPAI.PLNT_CD LEFT JOIN QM_PITM_SPEC QPS ON QPAP.PITM_SPEC_IDX = QPS.PITM_SPEC_IDX AND QPAP.PLNT_CD = QPS.PLNT_CD LEFT JOIN QM_PITM QP ON QPS.PITM_CD = QP.PITM_CD AND QPS.PITM_VER = QP.PITM_VER AND QPS.PLNT_CD = QP.PLNT_CD LEFT JOIN QM_PITM_INFO QPI ON QP.PITM_CD = QPI.PITM_CD AND QP.PITM_VER = QPI.PITM_VER AND QP.PLNT_CD = QPI.PLNT_CD LEFT JOIN QM_PITM_INFO_SAP QPIS ON QP.PITM_CD = QPIS.PITM_CD AND QP.PITM_VER = QPIS.PITM_VER AND QP.PLNT_CD = QPIS.PLNT_CD LEFT JOIN SY_APR_INFO SAI ON SAI.PLNT_CD = QPAP.PLNT_CD AND SAI.APR_IDX = QPAP.ASS_APR_REQ_IDX WHERE QPAR.PLNT_CD = #{plntCd} AND QPAP.ANS_PROC_CD = #{ansProcCd}
AND QPAP.ANS_TYP = #{ansTyp}
AND QPI.PITM_TYP = #{pitmTyp}
AND TRIM(UPPER(QPI.PITM_NM)) LIKE ('%' || TRIM(UPPER(#{pitmNm})) || '%')
AND TRIM(UPPER(QPI.PITM_CD)) LIKE ('%' || TRIM(UPPER(#{pitmCd})) || '%')
AND TRIM(UPPER(QPAR.LOT_NO)) LIKE ('%' || TRIM(UPPER(#{lotNo})) || '%')
AND TRIM(UPPER(QPAR.BATCH_NO)) LIKE ('%' || TRIM(UPPER(#{batchNo})) || '%')
AND QPAR.ETR_DT BETWEEN #{searchEtrDt[0]} AND #{searchEtrDt[1]}
ORDER BY QPAP.ANS_IDX, QPAR.REQ_IDX

SELECT NVL(MAX(ANS_IDX), 0) + 1 AS ANS_IDX
FROM QT_PITM_ANS_PROC
WHERE PLNT_CD = #{plntCd}

mapper.insert:
INSERT INTO QT_PITM_ANS_PROC(
    PLNT_CD
    , ANS_IDX
    , REQ_IDX
    , PITM_SPEC_IDX
    , ANS_PROC_CD
    , ANS_TYP
) VALUES (
    #{plntCd}
    , #{ansIdx}
    , #{reqIdx}
    , #{pitmSpecIdx}
    , #{ansProcCd}
    , #{ansTyp}
)

mapper.update:
UPDATE QT_PITM_ANS_PROC SET
    ANS_PROC_CD = #{ansProcCd}
    , REQ_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
    , REQ_DS = SYSDATE
    , REQ_DPT_CD = #{reqDptCd}
    , REQ_UID = #{reqUid}
    , REQ_RMK = #{reqRmk}
    , ANS_EDT = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
WHERE ANS_IDX = #{ansIdx}
AND PLNT_CD = #{plntCd}

UPDATE QT_PITM_ANS_PROC SET
    ANS_TYP = #{ansTyp}
WHERE ANS_IDX = #{ansIdx}
AND PLNT_CD = #{plntCd}
